css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bug.yourdomain.com") {
  html{  margin: 0 30px !important; font-family: Arial;}
  body{
    background-color: #f8fff8 !important;
    background-image: none!important; 
  }
  pre{ overflow: auto !important; }
  a{color: #0f5f0f !important; text-decoration:none;}
  a:hover{color: #0f5f0f !important; text-decoration:underline;}
  input[type=text]{border: 1px #cfcfcf solid; background-color: #ffffff; margin:1px;}
  input[type='submit']{ border:1px solid #cfcfcf; color:Black;   background-color: #f1f1f1; margin:1px;}
  textarea{border: 1px #cfcfcf solid; background-color: #ffffff; margin:1px;}
  .bz_comment {
    width: 100% !important;
    margin: auto !important;
  }
  #attachment_table, form > table { 
   width: 100% !important; 
   margin: auto !important;
  }
  #flags, #flags select{ width: 100% !important; }
  pre{ font-size: 1.05em !important; }
  div[align=center] a[href='quips.cgi']{display:none;}
  .bz_buglist{ border: 3px double #cfcfcf; }
  .bz_buglist tr th{ border-bottom: 1px solid #cfcfcf; font-weight:bold; background-color:#ffffff;}
  .bz_buglist td{ border-bottom: 1px solid #cfcfcf!important; }
  .bz_buglist td[class=first-child]{ border-right: 1px solid #cfcfcf!important; }
  #banner-version{display:none;}
  form[action='buglist.cgi']{white-space:nowrap;}
  div#useful-links div{border-bottom: 1px solid #cfcfcf;}
  div#useful-links div div{padding:4px !important;}
  div#footer{border: 3px double #c1c1c1 !important;}
}
@-moz-document url-prefix("http://bug.yourdomain.com/show_bug"){
  #header{border-bottom: 1px solid #cfcfcf;}
  form[name=changeform]{
    border-top: 1px solid #cfcfcf;
  }
  form[name=changeform] table tr td{font-size:12px; font-family: Arial;padding-left:2px;}
  form[name=changeform] table tr td[align=right]{white-space:nowrap;padding-right:2px;}
  form[name=changeform] > table { border: 3px double #cfcfcf; }
  tr > td > label[for=rep_platform]{}
  tr > td > label[for=op_sys]{}
  tr > td > label[for=version]{}
  tr > td > label[for=target_milestone]{}
  textarea#comment{width:100%;}
  /* Radio Buttons*/
  label[for|='knob']{font-size:12px;}
  input[id|='knob'][type='radio']{height:11px;}
}
