css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
  url-prefix("http://www.youtube.com/") 

{

/* fixed many section subheaders */
div [id*="vm-"] {background-color: transparent !important; }  

/* fixed footer */
#footer-container {background-color: transparent !important; }  

/* fixed my channel */
#edit_controls, #channel-body, .channel_tab_content,
#tab_contents_colors div[style*="background-color: rgb(238, 238, 238)"]
{background-color: transparent !important; }  
.theme_selected {background-color: #00c0c0 !important; }  

/* fixed account button background */
.yt-uix-button-menu-item {background-color: #000 !important; border: 0 !important; }  

/* fixed search suggestions */
#masthead-search-term {background-color: #000 !important; color: #00c0c0 !important; border-color: #000 !important; }
#masthead-search p {border: 1px solid #002020 !important;}
.yt-suggest-left, .yt-suggest-right, .yt-suggest-close {background-color: #000 !important; color: #00c0c0 !important; padding: 2px !important;}
.yt-suggest-left:hover {background-color: #002020 !important;}


/* improved buttons style */
.yt-uix-button {color: #00c0c0 !important; background: #001010 !important; border-color: #002020 !important; }
.yt-uix-button:hover, .yt-uix-button:focus {color: #fff !important;  border-color: #007070 !important; }

/* fixed inbox sidebar links */
a [id*="tab"] {color: #00c0c0; }
a.selected [id*="tab"], a:hover [id*="tab"]{color: #fff; }

/* fixed header on account settings */
#masthead-subnav { background: transparent !important; border: #002020 1px solid !important;}
h1, h2 { background-color: transparent !important; border: 0 !important; }

/* fix selected sidebar tab on account settings */
#yt-admin-sidebar a.selected, #yt-admin-sidebar .selected a {background: none repeat scroll 0 0 #123 !important; }

/* fix main content background when logged */
.fyf-container { background-color: transparent! important; border: #00c0c0 1px solid !important;}

/* fix background when moving on a video thumb on sidebar */
.video-list-item a:hover { background-color: #002020 !important; text-decoration: none !important; } 

/* fix on mouse hover comment background */
.current { background-color: #002020 !important; }

/* fix comment's author background */
.author-viewing { background-color: #123 !important; }

/* fix on mouse hover on video caption */
.watch-expander-head:hover { background-color: #002020 !important; }

/* fix video views counter */
.watch-expander-head-content { color: #fff !important; }

/* fixed color of main video caption */
#eow-description-short { color: #fff !important; }

/* fixed inbox background */
.statModule-item-line { background-color: transparent !important; }

/* remove main logo */
#logo {height: 0 !important; width: 0 !important; }

/* thumbnail border */
a.video-thumb { padding: 1px !important; background-color: #002020 !important; border: 0 !important; }

/* redefine global link */
a:link, a:visited, a:active, a:hover { color: #00c0c0 !important; text-decoration: none !important; } 

/* fix sidebar link color */
span.title { color: #00c0c0 !important; text-decoration: none !important; } 

/* fix featured video background */
.watch-ppv-vid {  background-color: #002020 !important; }

/* fix sidebar thumbnail border */
span.video-thumb {  background-color: transparent !important; border: #002020 1px solid !important;}

div.xxx, div.feedmodule-anchor,
#watch-headline-container, #watch-sidebar, 
#watch-container, #watch-video-container, 
#watch-main, #watch-main-container, 
#page, #content,
.video-list, .watch-module-body
{ color: #fff !important; background-color: transparent !important; }

div.yyy
{ color: #fff !important; background-color: #000 !important; }


/* remove icon and recolor suggestion alert */
div.yt-alert { background-color: transparent !important;}
img.icon { display: none !important; }


/* default behavior transparent background and white text */
html { color: #fff !important; background: #292722 url('data:image/png;base64,') left top repeat-x !important; }

body { color: #fff !important; background-color: transparent !important;}

h1, h2, h3 { color: #fff !important; }

}
