@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("deviantart.com") {
#deviantART-v7 #gmi-CCommentMaster form.f {border:1px solid #4F4F4F !important;}
#deviantART-v7 #gmi-CComment > div.ch-ctrl {background:none repeat scroll 0 0 #444 !important; border:1px solid #2D2D2D !important;}
#deviantART-v7 #gmi-CCommentThread .ccomment-post div.ch-ctrl {background:#444 !important;border: 1px solid #2D2D2D !important; border-top:0px !important; padding:0 8px 8px 8px !important;}
#deviantART-v7 #artist-comments .gr-headless .gr i.l {background:none !important; position:absolute !important;}
#deviantART-v7 #artist-comments .gr-headless .hh {background:none !important; border:none !important; margin:0 !important;}
#deviantART-v7 #artist-comments .gr-headless .hh > div {border:none !important; padding:2px 12px 0 !important;}
#deviantART-v7 #artist-comments .gr-headless .cc-name img {margin:0 !important;}
#deviantART-v7 #gmi-CComment > div.ch-ctrl .ch-ctrl.cc-in {background:-moz-linear-gradient(top, #4F4F4F, #353535 1px, #2B2B2B 20px, #2B2B2B) !important;}
div#gmi-CCommentThread > a[href^="http://comments.deviantart.com/4/"] {top:-10px;}
#deviantART-v7 #gmi-CComment > div.ch-ctrl .creason {color:#666 !important;}
#deviantART-v7 #gmi-CComment > div.ch-ctrl .creason em {text-decoration:none !important;}
}
