@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ::::: (code for Firefox 3.x) ::::: */

tab[busy] .tab-icon-image
{
  list-style-image: url("chrome://browser/skin/places/searching_16.png") !important;
}

/* ::::: Places Style Throbber (Code for Firefox 4 beta 1-6) ::::: */

#navigator-throbber[busy="true"],
#sidebar-throbber[loading="true"],
.tabbrowser-tab .tab-icon-image[busy],
.alltabs-item[busy] > .menu-iconic-left > .menu-iconic-icon {
    list-style-image: url("chrome://browser/skin/places/searching_16.png") !important;
}

/* ::::: (Code for Firefox 4 beta 7 +) ::::: */

.tab-throbber[progress], .tab-throbber[busy]
{
	list-style-image: url("data:image/png;base64,") !important;
}
