css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul); 

* { font-size: 10pt !important;} textarea, textbox { font-size-adjust: .56 !important;}

menupopup, popup { background-color: -moz-Dialog !important; cursor: auto !important;}

#urlbar, .searchbar-textbox { font-family: monospace !important; font-size-adjust: .49 !important;}

#urlbar { color: blue !important;} .searchbar-textbox { color: highlight !important;}

#PopupAutoComplete > .autocomplete-tree { background-color: Menu !important;}

@-moz-document url(chrome://browser/content/browser.xul) { tab[selected="true"] > hbox, 

tab[selected="true"] > toolbarbutton { font-weight: bold !important; background-color: highlight !important; 
                                       color: white !important;}}

#status-bar { border: none !important; margin: -2px !important; padding: 0 !important; 
              padding-left: 4px !important; padding-right: 2px !important; color: highlight !important;}

#status-bar > * , #status-bar > * > * { margin: 2px 0 2px 0 !important; padding: 0 1px 0 1px !important;}

.db_downbar, statusbarpanel, .statusbarpanel-iconic, .statusbarpanel-iconic-text, .statusbarpanel-menu-iconic,

.progressmeter-statusbar { -moz-appearance: none !important; border: none !important;}

.progressmeter-statusbar { background: white !important;} #btUrlText { color: highlight !important;}

#btTooltipTextBox, .tooltip-label { font-size-adjust: .56 !important; font-weight: 500 !important; 
                                    margin: -2px -3px -2px -3px !important; padding: 3px 5px 4px 6px !important; 
                                    background-color: InfoBackground !important; color: InfoText !important;}
