/**************************************
 * Style by Republicanito
 * http://www.lastfm.es/user/Republicanito
 * Just picked up http://userstyles.org/styles/2177 code and added other languages support                  
 * Original made by aabbqrstp *
 **************************************/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("last.fm"),
domain("lastfm.es"),
domain("lastfm.de"),
domain("lastfm.fr"),
domain("lastfm.jp"),
domain("lastfm.it"),
domain("lastfm.pl"),
domain("lastfm.com.br"),
domain("lastfm.se"),
domain("lastfm.tr"),
domain("lastfm.ru"),
domain("cn.last.fm") {


  /* Images
  ------------------------------------- */
  
  /*red_logo.gif*/
  img[src*="_logo.jpg"] {
          background: url('data:image/jpeg;base64,') top left no-repeat !important;
    width: 0 !important;
    height: 41px !important;
    padding-left: 207px !important;
  }

  /*red_gradient.gif*/
  #LastHeader {
    background: url('data:image/gif;base64,')   top left repeat-x #1f3cff !important;
  }
  
  /*red_rollover.gif*/
  #navigation li a:hover, #navigation li.current a {
    background:transparent url('data:image/gif;base64,R0lGODlhAwAsAIQgAEVa/0Vb/0Vd/0Vg/05l/1Np/1Nr/1hs/1xv/1xx/2Fx/2Zz/2Z2/2Z4/2t7/2t9/2t//3CB/3SE/3mH/3mK/36M/4OP/4OS/4OV/4OY/4ea/5ae/5ai/5an/5qr/5+s/yH+FUNyZWF0ZWQgd2l0aCBUaGUgR0lNUAAsAAAAAAMALAAABT7gJ3pkZ3Lopmps5mLwJVt0ZVP4pEt85EPAh9BBbBgZyIVSwUw4EdCD1ECtVgtYgnbAFXgB4IDYSy6bz2hyCAA7') repeat-x scroll left top !important;
  }
  
  /*top_red.gif*/
  div.lastPanel:not([class~="noHeader"]) div.h
  {
    background: transparent url('data:image/png;base64,') no-repeat scroll left top !important;
  }
  
  /*list.gif*/
  #LastWebsite a.textTog, #LastWebsite a.imgTog {
    background:transparent url('data:image/gif;base64,') no-repeat scroll left top !important;
  }
  
  /*minimise.gif*/
  #LastWebsite a.collapseTog, #LastWebsite a.expandTog {
    background:transparent url('data:image/gif;base64,') no-repeat scroll left top !important;
  }
  
  /* http://cdn.last.fm/depth/layouts/fixed_width_960/top_red.gif */
  body.fixedWidth {
    background-image:url('data:image/gif;base64,') !important;
  }
  
  /* http://cdn.last.fm/depth/layouts/fixed_width_960/top_bg.gif */
  body.fixedWidth #LastWebsite {
    background-image:url('data:image/gif;base64,') !important;
  }
  
  /* http://cdn.last.fm/depth/sidebars/panels/top_red_newuser.gif */
  div.lastPanel.empty div.h {
    background: none !important;
    background-image:url('data:image/png;base64,') !important;
  }
  
  /* http://cdn.last.fm/depth/homepage/overlay.gif */
  #thisIsScrobbling {
    background:transparent url('data:image/gif;base64,') no-repeat scroll left top !important;
  }
  
  /* http://cdn.last.fm/depth/header2/signup_red_left.gif */
  a#signupLink {
    background-image:url('data:image/gif;base64,') !important;
  }
  
  /* http://cdn.last.fm/depth/header2/signup_red_right.gif */
  a#signupLink span {
    background-image:url('data:image/gif;base64,') !important;
  }
  
  
  /* Misc
  ------------------------------------- */
  
  #headerBottom {
    border-top:1px solid #4e6aff !important;
  }
  
  #headerBottom a {
    color:#cfbeff !important;
    border-top: none !important;
  }
  
  #headerBottom li:not(#toglink) {
    border-right:1px solid #cfbeff !important;
  }
  
  #headerBottom a:hover, body.black #headerBottom a:hover {
    color:#FFFFFF !important;
    border-top: none !important;
  }
  
  #headerSearchbox input.sbox {
    border:1px solid #0032ff !important;
  }
  
  li#toglink {
    border-right: 0pt none !important;
  }
  
  ul.radioStations a.personal, div.sidebar ul.radioStations a.personal span, .errorBox, body .errorBox a, div.displayCopy h2 a, div.faqBox a, div.displayCopy h2, .instructBox h4, .greenBoxQuickFix h2, .faqBox h3, .greenBox span, .greenBox h2, #listenPageStarter input#userInput, div#newEvent .error label, #tom_contents .unknown, #LastWebsite table.previewPlaylist tr.veryManyListeners td.subject a, body.fixedWidth div.top input#favArtist, body.fixedWidth h1, table.recentList tr.now td, div.displayCopy h2, .instructBox h4, .greenBoxQuickFix h2, .faqBox h3, .greenBox span, .greenBox h2, table.recentList tr.lastHour td.date, h3, h3 a, h3 a:link, h3 a:visited, h3 a:hover, #listenPageStarter input#userInput  {
    color:#1f3cff !important;
  }
  
  a.pdfDownload:hover span {
    border-bottom:1px solid #1f3cff !important;
  }
  
  div#widgetConfig dl dd img.red, body .errorBox a:hover {
    background:#1f3cff none repeat scroll 0% !important;
  }
  
  div#quiltConfigurator div#moreOptions label.red img, table.recentList tr.now td {
    background-color:#1f3cff !important;
  }
}
