css:
@-moz-document url(chrome://browser/content/browser.xul){

tab[pinned] .informationaltab-thumbnail-container{display:none!important}
tab[pinned] .tab-icon-image{}

tabs tab[pinned] .tab-label{
visibility:hidden!important;
-moz-transform:rotate(-90deg) translate(-12px)!important;
margin-right:-40px!important;
margin-left:-60px!important;width:100px!important;
}

tabs:hover tab[pinned] .tab-label{visibility:visible!important;}

.tabbrowser-tabs{max-height:30px!important}

#main-window[sizemode="maximized"]:not([inFullscreen])[tabsontop="true"] .tabbrowser-tabs{
margin-bottom:-2px!important
}

#main-window:not([sizemode="maximized"]):not([inFullscreen])[tabsontop="true"] .tabbrowser-tabs{
margin-bottom:-1px!important
}

#main-window:not([sizemode="maximized"])[inFullscreen][tabsontop="true"] .tabbrowser-tabs{
margin-bottom:-2px!important
}


.tabbrowser-tab{overflow:hidden!important}
.tabbrowser-tabs:hover{max-height:300px!important}

.tabbrowser-tabs .tabbrowser-tab .informationaltab-thumbnail-container{
	visibility:hidden!important;
	overflow:hidden!important;
	border-radius:8px!important;
	border:none!important;
	-moz-box-shadow:gray 0 0 8px -2px inset, white 0px 0px 8px 1px, black 0px 0px 1px 1px!important;
	height:0px!important;
	width:0px!important;
	max-height:0px!important;
	max-width:0px!important;
}
/*.tabbrowser-tab,.tabbrowser-tab *:not([class="tab-icon-image"]){width:auto!important}*/

.tabbrowser-tabs:hover .tabbrowser-tab .informationaltab-thumbnail-container{
	visibility: visible!important;
	height:100px!important;
	width:150px!important;
	max-height:100px!important;
	max-width:150px!important;
	margin-top:5px!important;
	margin-bottom:4px!important;

}

label.tab-text{width:auto!important}

.tab-progress{display:none!important}

.tab-close-button,
.tab-icon-image,
.tabbrowser-tabs,
.informationaltab-thumbnail-container
{
	-moz-transition-property: all!important;
	-moz-transition-duration:.5s!important;
	-moz-transition-delay: 0s!important;

}

tab:not([pinned]) .tab-close-button{
	display:block!important;
	visibility:visible!important;
	opacity:1!important;
	padding-right:2px!important;
	padding-left:0px!important;
	margin-left:0px!important;
	margin-right:0px!important;
}
tabs:hover tab:not([pinned]) .tab-close-button{
	display:block!important;
	visibility:visible!important;	
	margin-top:-110px!important;
	opacity:1!important
}
.tabbrowser-tabs:hover .tab-icon-image{margin-bottom:108px!important;opacity:1!important}

}
