@author: charge
@date: 19.9.2010

Pohjana Telkku.com Simplified by Jani Ollikainen (http://userstyles.org/styles/4528)
*/

#contextMenuRight,#contextMenuLeft{background:#000!important;}
#header #contextMenu{background:#000!important;border-bottom:10px solid #20B2AA!important;}
#header #contextMenu div.dropMenuHandleContainer a.dropMenuHandle,div#programInfoContainer div#programInfoWindow div#main,div#programInfoContainer div#programInfoWindow div#main div.buttons a,.dropMenu a{color:#000!important;}
#header #contextMenu ul li{background:#000!important;border-left:1px solid #333!important;}
#header,#header #topShelf,#header #headerContent,#header #contextMenu,#footer,.separatorLine{min-width:0!important;}
#jello-content > #mainContent{min-height:0!important;}
#mainContent{background:none!important;height:auto!important;}
#ohjelmataulu a.s{color:#20B2AA!important;}
#ohjelmataulu td{border:1px solid #282828!important;}
#ohjelmataulu td table td{border:none!important;}
#ohjelmataulu tr.tuleeNyt td{background:#181818!important;}
#programInfoContainer,#opacityOverlay{background:transparent!important;}
#tickerBanner,#tickerBannerWrapper,#leftColumn,#footer,#header #topShelf,#header #headerContent,#footer,#topBanner,#hartwallFlash,#elisaFlash,#otiFlash,#ads,#il_video,.separatorLine,#mainContent table[width="100%"]{display:none!important;}
a,label{color:#FFF!important;}
a:hover{color:#ccc!important;}
body{background:#000!important;color:#ccc!important;}
div#programInfoContainer div#programInfoWindow{margin:0 auto;}
div#programInfoContainer div#programInfoWindow a#closeWindow{top:0;}
html{margin-bottom:0!important;}
h3{background:black!important;}
.previous{text-indent: -9999px;}
.next{text-indent: -9999px;}


}
