css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("dis.4chan.org") {

.rules,
.logo+hr,
.logo+hr+div,
.logo+hr+div+hr,
hr+script+center>a,
hr+script+center+hr,
#recaptcha_logo,
#recaptcha_tagline,
table+br+hr+center>a,
table+br+hr+center+hr,
#threadlist,
.doubledash {display:none !important}

* {
    border: none !important;
    background: none !important;
    color: inherit !important;
    font-family: sans-serif;
}
.hborder,.hborder * {
    margin: 0 !important;
    padding: 0 !important;
}
.midhead {
    font-size: 0.01em !important;
    letter-spacing: -.5em;
}
form[action="derefer"] {
    font-size: 100em !important;
    letter-spacing: 0;
}
form[action="derefer"] select {
    -webkit-border-radius: 3px;
    border: 1px solid #333 !important;
    background: #111 !important;
    margin: 0 8px !important;
}
form[action="derefer"]+form {
    display: none !important;
}
html {
    color: #777 !important;
    background: #222 url('http://i51.tinypic.com/30il5ki.png')
        fixed bottom right no-repeat !important;
}
a {
    color: #9f0 !important;
}
.postheader {
    background: rgba(0,0,0,0.5) !important;
}
blockquote {
    color: #fff !important;
}



.post:nth-child(2n) {
    background: -moz-linear-gradient(top,
        rgba(255,255,255,0.1),
        rgba(255,255,255,0.0)) !important;
    background: -webkit-gradient(
        linear,left top,left bottom,
        from(rgba(255,255,255,0.1)),
          to(rgba(255,255,255,0.0))) !important;
}
.post,
.post:first-child {
    background: -moz-linear-gradient(top,
        rgba(0,0,0,0.4),
        rgba(0,0,0,0.0)) !important;
    background: -webkit-gradient(
        linear,left top,left bottom,
        from(rgba(0,0,0,0.4)),
          to(rgba(0,0,0,0.0))) !important;
    padding: 4px 8px !important;
}

.postername {
    color: #bbb !important;
    font-weight: normal !important;
}
a[href="mailto:sage"] {
    color: #fc0 !important;
    text-decoration: none !important;
}
a[href="mailto:sage"]:before {
    content: 'â¬Š';
    color: #f00 !important;
}

.hidden {
    padding-left: 1em;
}
.hidden br {
    display: none;
}
.hidden a {
    font-size: 0.01em;
    letter-spacing: -.5em;
    text-decoration: none !important;
    color: rgba(0,0,0,0) !important;
}
.hidden a:before {
    font-size: 100em;
    letter-spacing: 0;
    content: 'read it mang';
    color: #9f0 !important;
}
.spoiler {
    color: #444 !important;
    background: #000 !important;
}
.spoiler:hover {
    color: #fff !important;
}
}
