css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('wikipedia.org'){

li   {  font-family: Candara, georgia !important;
        font-size: 10pt !important;
        letter-spacing:1px;
        text-decoration: none !important;
        line-height:20px !important; 
        margin-left: 40px !important;}

P   {   font-family: Candara, georgia !important;
        font-size: 11pt !important;
        letter-spacing:1px;
        text-decoration: none !important;
        padding:20px 80px 0 40px !important;}

.thumbinner {margin:10px 20px 10px 20px !important;}

.firstHeading {
        font-size:50px !important;
        font-family: georgia !important;
        font-style:italic !important;
        font-weight:bold !important;
        color:#c5bc98 !important;
        padding-bottom:10px !important;
        margin-bottom:70px !important;
        text-shadow: 1px 1px 1px #666;
        text-align:center;}

.mw-headline {
        font-family: georgia !important;
        font-style:italic !important;
        color: #333 !important;}

h3 {margin-top:40px !important;}

h2 {padding-top:50px !important;}

#mw-panel {display:none !important;}

#mw-head {display:none !important;}

#footer {display:none !important;}

#mw-head-base {display:none !important;}

#mw-page-base {display:none !important;}

#content {
-webkit-box-shadow: 0px 0px 80px #999;
-moz-box-shadow: 0px 0px 80px #999;
box-shadow: 0px 0px 80px #999;
margin:0px auto !important; 
width: 1100px !important;
padding:40px !important;
background: -webkit-gradient(
    linear,
    left top,
    left bottom,
    color-stop(0.0, rgb(250,248,220)),
    color-stop(0.05, rgb(255,255,255)),
    color-stop(0.6, rgb(250,248,220))
)!important;
background: -moz-linear-gradient(
    center top,
    rgb(250,248,220) 0%,
    rgb(255,255,255) 5%,
    rgb(250,248,2205) 60%
) !important;
}

.toc {
padding:20px !important; 
font-size: 9pt !important;        
font-family: tahoma !important;
letter-spacing:1px;
margin-top:40px !important;
}

.toclevel-1 {line-height:22px !important;}

table {
font-size: 8pt !important;        
font-family: tahoma !important;
letter-spacing:1px;margin:5px;
}

.wikitable {font-size: 8pt !important;        
font-family: tahoma !important;
letter-spacing:1px;margin:5px;
}

.thumbcaption {margin:5px;}

.ambox {margin-bottom:60px !important;}

.infobox {padding:10px !important; 
margin-left:30px !important;
font-size: 8pt !important;        
font-family: tahoma !important;
letter-spacing:1px !important;
margin:5px; 
line-height:15px !important;}

.infobox img {margin:15px !important;}

.vertical-navbox {
padding:10px !important; 
margin-left:30px !important;
font-size: 8pt !important;        
font-family: tahoma !important;
letter-spacing:1px !important;
margin:5px; 
line-height:15px !important;}

.mediawiki {
background-image: none !important;
background-color:#dfddc8 !important;}
}
