css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("xing.com") {

/* http://www.mprove.de/script/10/xing/, 20-Dec-2012, v2.8 */

/* gray endorsement */
.endorse .tag .action { background: #d4dcda !important; color:gray !important;}
.arrow{display:none !important;}

/* Activity: new connection */
li[data-activity-cat="7"] .controls,
li[data-activity-cat="7"] ul.actions {display:none;}

/* Focus on headlines at the news feed */
#maincontent .descriptive-list.post-list,
#maincontent .descriptive-list.post-list strong,
#maincontent .descriptive-list.post-list strong a,
.myxing-container .date {
    color: #abc !important;
    font-weight: normal;
    }
  
#maincontent .descriptive-list.post-list .icn,
#maincontent .descriptive-list.post-list .date { display: none !important; }

#maincontent .descriptive-list.post-list li:first-child .date,
#maincontent .descriptive-list.post-list li.last .date { display: inline !important; }

.myxing-container .descriptive-list .description { color:#000 !important; }

/* groups home: no redundant title, no no news items */
.groups-callout + h1,
.groups-callout + .flip-switch-wrapper h1,
#news-items p,
#active-threads h2 {display:none !important;}

/* form teaser: hide own photo */
#form-teaser .photo, #form-teaser strong, #form-teaser .icn, #form-teaser .copy-light {display:none !important;}


/* End of Xing Design Hack */
}
