css:
/*Script hecho por Chikitown(ertonio3, asierralavado)*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.tuenti.com") {

	#main_info{
		background: none !important; color: #0099ff !important;
	}


	/*FONTS*/
	*{
		font-family: "Ubuntu Titling Rg"!important;
	}

	html, body, #nonfooter, #booklet, #content, .UIFullPage_Container, .home, #facebook, .profile{
		font-family:"Ubuntu Titling Rg" !important;
		color:#000000 !important;
	}

	/*BARRA DE TUENTI*/
	#sections>li>a{
		background: none!important;
	}
	#header,#sections>li>a:hover,#sections>li>.active,.settings>li>a:hover,.overlayBody{
		background: url("http://img375.imageshack.us/img375/5521/ausaero.png") !important;
		-moz-background-clip: padding !important;
		border: 2px solid transparent !important;
		-moz-border-bottom-colors: rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-left-colors: rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-right-colors: rgba(0,0,0,.62) #e3e7ea rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-top-colors:  rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-radius: 6px !important;
	}
        .uploadPhotos>.notranslate{
                background: none !important;
        }

	/*ARREGLO CHAT*/
	#chat_roster_module{
		background-color:#FFFFFF;
	}
	#chat_home_roster{
		background: none !important;
	}
        .chatDockMenu,.chatDockMenu>div{
                background: none !important;
                border:0px!important;
        }

        .chatDockDialogViewportFlow{
                background: rgba(255,255,255,0.3) !important;
                border:0px!important;
        }

        .chatDockMenu>div>a{
                color:#000000 !important;
        }

	/*FONDO*/
	.notranslate {
		background: url("http://img405.imageshack.us/img405/3120/fondotuenti.png")!important;
	}

	/*BORDES WIN 7*/
	.layout-1,.layout-2,.layout-3,.layout-4,.layout-5,.chatDockItems,#loading,#footer,#chat_roster_module,.sidebar.left,#avatar .body,#chat_header_menu,.chatDockMenu>div>a,.chatDockMenu,.chatDockDialogTextarea,.sidebar.right>div{
		background: rgba(255,255,255,0.3) !important;
		-moz-background-clip: padding!important;
		border: 6px solid transparent!important;
		-moz-border-bottom-colors:  rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-left-colors: rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-right-colors: rgba(0,0,0,.62) #e3e7ea rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-top-colors:  rgba(0,0,0,.62) #f5f8fa rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3) rgba(255,255,255,0.3)!important;
		-moz-border-radius: 6px !important;
	}

	/*ARREGLO ANCHO*/
	.layout-1,.layout-2,.layout-3,.layout-4,.layout-5{
		position:relative!important;
		width:80%!important;
	}

	.sidebar.right{
		position:relative!important;
		width:23%!important;
	}

	#avatar>.body{
		position:relative;
		left:-15px;
		width:100%!important;
		z-index:0;
	}

	#avatar>.action{
		position:relative;
		width:100%!important;
		z-index:1;
	}

	.chatGamesTextarea{
		position:relative;
		z-index:0;
	}

	.overlayBody{
		position:relative;
		width:100%!important;
	}

	.overlay{
		position:relative;
		padding-right:10px!important;
	}

        .headerContent{
                position:relative;
                width:80%!important;
        }

        .chatDockDialogViewportFlow{
                position:relative;
                width:100%!important;
        }

	/*SIDEBAR*/
	.sidebar.left{
		background: url(http://img217.imageshack.us/img217/4379/asddasdwesd.png)!important;
	}


	/*SOMBRAS*/
	#header{
		-moz-box-shadow: -3px 3px 5px -1px black;
		-webkit-box-shadow:black -3px 3px 5px -1px;
	}

	.layout-1,.layout-2,.layout-3,.layout-4,.layout-5,.chatDockItems,#loading,#footer,#chat_roster_module,.overlayBody{
		-moz-box-shadow: 3px 3px 5px -1px black;
		-webkit-box-shadow:black 3px 3px 5px -1px;
	}


	/*ARREGLO FOOTER*/
	#footer{
		position:relative!important;
		left:0px!important;
		top:0px!important;
		z-index:100!important;
	}

	/*LOGO CHIKITOWN*/
	.header a.logo{
		background-image: url('http://img819.imageshack.us/img819/1512/logoam.png') !important;
		background-color: transparent !important;
			width: 180px !important;
	}
}
