css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("23hq.com") {
body, div, td
{
  background:none !important;
}

input, textarea {
  background-color:#282828 !important;
}

select {
  background-color:#282828 !important;
}

h4 {
  color:white !important;
}


span b u {
  text-decoration:line-through !important; font-weight:normal !important;
}
body, #allcontainer2, #titlebar 
{
  background-color:#111 !important; color:#ccc !important 
}
#allcontainer1 
{
border:0 !important
}
#allcontainer2 
{
border:0 !important;
}
#photoCaption, .phototext, #photoCaption p, .phototext p, #content h2 
{
color:#eee !important
}
#topcontainer 
{
margin-bottom:20px !important;
}
#_toolbar 
{
background-color:#444 !important; margin-bottom:10px !important; height:16px !important; width:8px !important; overflow:hidden !important; padding:0 !important;
}
#_toolbar:hover 
{
background-color:#444 !important; margin-bottom:10px !important; height:50px !important; width:98% !important; padding:0 !important;
}
#titlebar 
{
margin-bottom:10px !important
}
#topcontext, #allcontainer1 
{
width:780px !important;
}
#bottomcontext 
{
margin-bottom:40px !important
}
#about 
{
text-align:left !important;
}
#tabs td div 
{border:0 !important; padding:3px !important}
#tabs td div:hover, #tabs td div:hover a 
{background-color:#999 !important; color:#fff !important}
#tabs td {
background-color:#666 !important; border:0 !important;}
#tabs td a 
{color:#ccc !important;}
#tabs td a:hover 
{ color:#fff !important;}
#tabs td.active 
{background-color:#999 !important;}
#tabs td.active a 
{color:#fff !important;}
#groupContainer 
{color:#999 !important;}
#calendar td.empty, .calendar td.empty 
{background-color:#444 !important;}
#calendar td, .calendar td 
{background-color:#666 !important;}
#sidebar .right .text 
{color:#000 !important; background-color:#222 !important; width:200px !important}
#search_input 
{font-size:10px !important;}

table.form .form-element .form-label, table.form .form-element .form-label-error 
{width:200px !important}

.widelist .odd 
{background-color:#444 !important}
.widelist .even 
{background-color:#666 !important}
.linkbutton, #contextlinks a, .largelinkbutton 
{background-color:#666 !important; border:1px solid #999 !important; color:#ccc !important}

.linkfoot *
{font-size:10px !important; visibility:hidden !important; height:0 !important}
.linkfoot 
{visibility:hidden !important; height:0 !important}
.public 
{font-size:10px;}
.image 
{min-height:45px !important; max-height:135px !important; padding:0 !important;}

.column 
{padding:10px !important; padding-bottom:0 !important}
.colum .text 
{border:0 !important}
.column .date, .column .date a 
{visibility:hidden !important; height:0 !important}

.photo img 
{border:1px solid #444 !important; padding:2px !important}

h1, .h1 
{color:#999 !important}
h3, .h3 
{border-bottom:1px solid #444 !important}

#conversations .even, #conversations .odd 
{background-color:#111 !important; border:1px solid #444 !important}
#content textarea 
{height:200px !important; background-color:#ccc !important}

#albumContainer a 
{color:#fff !important}
#main #sidebar.left 
{background:none !important}
#main #sidebar 
{background:none !important}
#sidebar div.graygradient 
{background:none !important}
#sidebar #tag .odd 
{background-color:#333 !important}
#sidebar #tag .even 
{background-color:#666 !important}
#sidebar #tag .odd a, #sidebar #tag .even a 
{color:#fff !important}
#main #sidebar.right 
{border:0 !important; background-color:#111; padding:4px !important;}
#main #sidebar.right .text h3 
{color:#fff !important;}
#main #sidebar.right .text td a 
{background:none !important; background-image:none !important; color:#fff !important}
#main #sidebar.right .text td 
{padding:0 !important; margin:0 !important}
#main #sidebar.right .text img 
{border:1px solid red !important; visibility:hidden !important}
#main #sidebar.right .text a img 
{border:1px solid green !important; visibility:visible !important}
#main #sidebar.right .text img.simplealbumframe, #main #sidebar.right .text img.simplestoryframe 
{border:0 !important; visibility:visible !important}
}
