/* ==UserStyle==
@name           Dark LWN
@namespace      USO Archive
@author         Serkan Hosca
@description    `Dark LWN`
@version        20160116.18.27
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lwn.net") {

    ::-moz-selection {
        background: orangered;
        color: #000;
    }

    html,
    body {
        padding: 0 !important;
        margin: 0 !important;
        font: 11pt sans-serif !important;
    }

    table,
    tr,
    span,
    p,
    td,
    center,
    p,
    h1,
    font {
        color: inherit !important;
        background-color: inherit !important;
        font-family: inherit !important;
        text-shadow: black 0px 1px 3px !important;
    }

    html,
    body,
    div,
    .navmenu ul {
        background: #1d1f21 !important;
        text-shadow: black 0px 1px 3px !important;
        color: #d0d0d0 !important;
    }

    a {
        color: #81a2be !important;
        text-shadow: black 0px 1px 3px !important;
    }

    .TextAd,
    .AdRight {
        display: none !important;
    }

    .CommentBox {
        border-color: #cc6666 !important;
    }

    .SummaryHL {
        text-align: center !important;
        text-decoration: none !important;
        font-size: 95% !important;
    }

    .GAByline,
    .FeatureByline,
    .PullQuote {
        border: 1px dotted #cc6666 !important;
        background-color: #81a2be !important;
        font: inherit !important;
        float: right !important;
        padding: 5px 10px !important;
        margin: 0 10px !important;
        width: 25% !important;
        text-align: center !important;
        color: black;
    }

    .bq,
    pre {
        /*width: 80em !important;*/
        margin: 0 20px !important;
        padding: 5px 25px !important;
        background-color: #373b41 !important;
        font: 10pt monospace !important;
        color: #d0d0d0 !important;
    }

    .QuotedText {
        color: #c5c8c6 !important;
    }

    .FormattedComment pre {
        background-color: white !important;
        padding: 0 !important;
        margin: 0 !important;
    }

    .ArticleText span {
        color: #cc6666 !important;
    }

    .CommentPoster,
    .BlurbListing font {
        color: #b294bb !important;
    }
    .ArticleText input,
    .CommentReplyButton input {
        color: #b5bd68 !important;
        background: transparent !important;
        border: none !important;
    }

    .CommentTitle {
        background-color: #373b41 !important;
    }

    body > center > p > font {
        padding: 0 !important;
        margin: 0 !important;
        border: none !important;
        display: block !important;
    }

    input {
        color: #000 !important;
    }
}
