#wiki-page h3{background:#b0b0b0  !important; 
border-bottom-left-radius: 1em 1em  !important;
border-bottom-right-radius: 1em 1em  !important;
border-top-left-radius: 1em 1em  !important;
border-top-right-radius: 1em 1em  !important;
-moz-border-radius: 1em 1em !important;
width: 97%  !important; padding-right: 1em  !important; padding-left: 1em  !important;padding-top: 0em !important; border-color: #000000  !important; font-weight: bold  !important; border-style: solid  !important; border-width: 1px !important;}
#user-menu{margin: 0px !important}
.toclevel-1:hover a, .toclevel-2:hover a{border-bottom: 0px solid  !important; text-decoration: underline  !important; color: #000000 !important}

#wiki-page table{width: 100% !important;}
#content-article ol li a:hover{border-bottom: 0px solid  !important; text-decoration: underline  !important; color: #000000 !important;}
#wiki-content {background:#b0b0b0  !important;}
#wiki-page {background:#b0b0b0  !important;}
#toc {background:#b0b0b0  !important;border: 1px solid  !important;}
}
