css:
@-moz-document domain('boards.4chan.org'), url-prefix('http://boards.4chan.org/'){
/* Boards and Threads */

a, img, input, .pages, #recaptcha_area, body center{visibility: visible !important;}

td, td a{
	border: none;
	text-decoration: none;
}

*{
	font-size: 12px !important;
	font-family: "Helvetica Neue", Helvetica, Arial, Sans-Serif !important;
	line-height: 18px;
}

html, body{
	background: #fff !important;
	color: #444 !important;
	font-style: normal;
	font-weight: normal;
	font-size: 8pt !important;
	line-height: 10px;
}

blockquote{
	margin: 20px 0;
}

img{
	padding: 0px;
	margin: 0px 6px 2px 2px;
	-webkit-transition: -webkit-box-shadow .5s linear;
	-moz-transition: -moz-box-shadow .5s linear;
}

img:hover{
	-webkit-box-shadow: 0px 0px 10px rgb(109, 164, 255);
	-moz-box-shadow: 0px 0px 10px rgb(109, 164, 255);
	-webkit-transition: -webkit-box-shadow .5s linear;
	-moz-transition: -moz-box-shadow .5s linear;
}

#logo{
	background: none;
	padding-top: 6em;
	height: 50px;
	line-height: 1px !important;
}

.backlink{
	color: #000;
	text-decoration: none !important;
	padding-left: 5px;
}

a[href*="res"], .quotelink{
	color: rgb(0,0,0) !important;
	text-decoration: none !important; 
}



a[href*="res"]:hover, .backlink:hover{
	color: rgba(0,0,0,.3) !important;
	text-shadow: 0px 0px 1px rgb(109, 164, 255), 0px 0px 4px rgba(109, 164, 255,.8);
}

.quotelink{
	margin-bottom: 14px !important;
}

.unkfunc{
	color: #90c576;
}

table[align="right"], .postarea{
	display: block;
	border: 1px solid rgba(0,0,0,.5);
	background-color: rgb(250,250,250);
	position: fixed;
	right: -311px;
	top: 20px;
	opacity: .1;
	padding: 0;
	width: 400px;
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	-webkit-transition: opacity .5s linear, right .2s linear;
	-moz-transition: opacity .5s linear, right .2s linear;
	-webkit-box-shadow: 0px 0px 10px rgba(0,0,0,.1);
	-moz-box-shadow: 0px 0px 10px rgba(0,0,0,.1);
}

table[align="right"]{
	top: 600px;
}

div.postarea:hover{
	opacity: 1;
	right: 20px;
	-webkit-transition: opacity .5s linear, right .2s linear;
	-moz-transition: opacity .5s linear, right .2s linear;
	z-index: 999;
}

table[align="right"]:hover{
	opacity: 1;
	right: 37px;
	-webkit-transition: opacity .1s linear, right .2s linear;
	-moz-transition: opacity .1s linear, right .2s linear;
}

input[type="password"], input[type="password"]:hover{
	width: 50px !important;
}

form{
	margin: 1em;
}

form>table{
	margin: 1em 0;
}

input[value="delete"]{
	padding-right: 4px;
}

table[align="right"] tbody tr:first-child td{
	padding: 3px 0 0 10px;
}

table[align="right"] tbody tr:first-child td input:nth-child(n+5), #com_submit{
	color: #222 !important;
	border: none;
	background-color: #fff;
	padding: 2px 0px;
	margin: 0 0 0 2px !important;
	width: 76px !important;
}

.inputtext{
	padding: 2px 0 0;
}

.inputtext, textarea{
	width: 300px;
	border: none !important;
	margin-bottom: 3px;
}

textarea{
	height: 300px !important;
}

font[size="5"] b span{
	letter-spacing: 0.5px;
	color: #000;
	font-size: 36px !important;
	font-weight: bold;
	margin: 0 auto;
	top: -2px;
	text-shadow: 0px 1px 0px rgba(255,255,255,1), 1px 0px 0px rgba(255,255,255,.6);
	padding: 2px;
}

td.reply, td.replyhl{
	max-width: 900px;
}

td.reply blockqoute, td.replyhl blockqoute{
	margin: 0;
}

td.reply{
	background-color: rgb(250,250,250) !important;
	border: 1px solid rgba(0,0,0,.1) !important;
	color: #333 !important;
	padding: 15px !important;
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	position: relative;
}

.postarea form table tbody tr{
	padding: 0 0 7px 0 !important;
}

.postarea form table tbody tr:nth-child(3) td input[type="text"]{
	width: 220px;
}

.postarea form table tbody tr:nth-child(3) td input[type="submit"]{
	width: 79px !important;
	color: rgba(109, 164, 255,1);
	background-color: rgb(109, 164, 255,.1) !important;
}

input[type="file"]{
	display: block;
	width: 300px !important;
}

#backwash-viewport{
	background: none !important;
	border: none !important;
}

#backwash-viewport table tr td{
	-webkit-box-shadow: 0px 0px 10px rgba(0,0,0,.1);
	-moz-box-shadow: 0px 0px 10px rgba(0,0,0,.1);
}

#backwash_viewport a img, td.reply a img, td.replyhl a img, form a img{
	margin: 10px 20px 5px 0;
}

#backwash-viewport table tr td, td.replyhl{
	background-color: rgb(240,240,255) !important;
	border: 1px solid rgb(109, 164, 255) !important;
	padding: 15px !important;
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
}

td.postblock{
	background: none !important;
	border: none !important;
	color: #000 !important;
	text-align: right;
}

tr[heigh="73"]:nth-child(n+1){
	display: none;
}

#recaptcha_area{
	position: relative;
	left: -7px;
}

td.recaptcha_image_cell{
	padding: 0 !important;
}

#recaptcha_image img, #recaptcha_image img:hover, #recaptcha_response_field{
	border: none !important;
}

.pages{
	border: none !important;
	background: none;
}

.pages tbody tr a, .pages tbody tr b{
	color: #000;
	padding: 4px;
	font-size: 18px;
}

.pages tbody tr a:hover, .pages tbody tr b{
	background-color: #fff;
	color: rgba(0,0,0,.3) !important;
	opacity: 1;
	text-shadow: 0px 0px 1px rgb(109, 164, 255), 0px 0px 4px rgba(109, 164, 255,.8);
}

#header{
	opacity: .2;
	-webkit-transition: opacity .5s linear;
	-moz-transition: opacity .5s linear;
}

#header:hover{
	opacity: 1;
	-webkit-transition: opacity .5s linear;
	-moz-transition: opacity .5s linear;
}

#navtop, #navtop a{
	text-decoration: none;
	float: none !important;
	font-size: 10px !important;
}

.boxbar, #ft, .yui-g, table[align="right"] tbody tr:nth-child(2), .postarea form>div, .pages tbody tr form, .pages tbody tr td input, .filesize, #recaptcha_table tbody tr td:nth-child(n+2), #filter-container, #ft, .yui-g, form>center, body>center,div[style="text-align: center"], img[width="300"][height="100"], span[style="left: 5px; position: absolute;"], .bf, table[width="100%"], small, .doubledash, #ft, #filter-button, #option-button, #navtopr, #navbotr, font[size="1"], #footer, .pages td:first-child, .pages td:last-child, .rules, .pages input, hr{display: none !important;}

/* Homepage */

#doc #hd #logo{
	height: 100px;
	padding: 0;
	margin: 0 auto;
}

#logo h1{
	margin-left: 0px !important;
}

#logo h1 a{
	height: 100px;
	padding-right: 0px !important;
	font-size: 48px !important;
	text-align: center;
	margin-top: 50px;
}

a{color: black !important;}

a:hover{
	color: rgba(0,0,0,.3) !important;
    text-decoration: none !important;
    text-shadow: 0px 0px 1px rgb(109, 164, 255), 0px 0px 4px rgba(109, 164, 255,.8);
}

.boxcontent{
    background: none;
    padding-top: 2em;
    font-size: 8px;
}

.box-outer{
    border: none;
    background: none !important;
	background-color: rgb(240,240,240) !important;
	border: 1px solid rgba(0,0,0,.1) !important;
	color: #333 !important;
	padding: 15px !important;
	-webkit-border-radius: 5px;

}

.column{
    width: 140px !important;
    float: none;
    display: table-cell;
    text-align: center;
}

h3{
    font-size: 12px;
    color: black;
    padding-bottom: 15px !important;
}

h3:nth-child(3){
	padding-top: 10px;
}

sup{
	color: rgb(109, 164, 255);
	vertical-align: middle;
}
}
