css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com"), domain("upload.youtube.com") {
a  {
color:#EE0000!important;
text-decoration:none;
text-shadow: 0em 0em 0.2em #FF0000;
}

.grid-view .video-facets span, .grid-view .channel-facets span, .list-view .playlist-main-stats span, .grid-view .movie-facets span, .grid-view .trailer-facets span, .grid-view .show-facets span {
display:block;
padding-left:1px;
color:#FF5555;
text-shadow: 0em 0em 0.2em #FF0000;
}

.yt-alert-announce, .yt-alert-info, .yt-alert-status {
background-color:#FFDDDD!important;
}

#chrome-promo-hp .tagline {
color:#EE0000 !important;
font-size:13px;
font-weight:normal;
}

.video-list-item .stat {
color:#FF5555 !important;
display:block;
font-size:0.9166em;
height:1.4em;
line-height:1.4em;
overflow:hidden;
}

.video-list-item .title {
color:#EE0000 !important;
cursor:pointer;
display:block;
font-size:1.0833em;
font-weight:normal;
line-height:1.2;
margin-bottom:2px;
max-height:32px;
overflow:hidden;
width:180px;
}

a.video-list-item-link:hover, .hover a.video-list-item-link {
background:none repeat scroll 0 0 #FFEEEE!important;
text-decoration:none;
}

#watch-sidebar h4.first {
padding-top:3px;
}

.watch-module-body h4 {
border-bottom:2px solid #FF0000 !important;
color:#EE0000!important;
font-size:1.0833em;
font-weight:normal;
margin-bottom:1em;
margin-top:0;
padding-bottom:4px;
text-shadow: 0em 0em 0.2em #FF0000
}

html, body, div, span, img, applet, embed, canvas, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, ins, kbd, q, samp, small, strike, strong, sub, sup, tt, var, b, u, i, s, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, button, table, caption, tbody, tfoot, thead, tr, th, td {
background:none repeat scroll 0 0 transparent;
color: #EE0000 !important;
border:0 none;
font-size:100%;
margin:0;
outline:0 none;
padding:0;
text-shadow: 0em 0em 0.2em #FF0000
}

.comment-reactiontaglink  {
-moz-border-radius:3px 3px 3px 3px;
background-color:#FFDDDD!important;
border:1px solid #DD0000!important;
color:#FFFFFF;
display:inline;
font-size:10px;
font-weight:bold;
line-height:1em;
margin-right:1px;
padding:0 3px;
text-decoration:none;
}

.yt-uix-button {
-moz-border-radius:3px 3px 3px 3px;
background:-moz-linear-gradient(center top , #FFEEEE, #FFBBBB) repeat scroll 0 0 #FF0000 !important;
border:1px solid red;
color:#000000;
cursor:pointer;
height:2.0833em;
overflow:visible;
padding:0 0.5em;
vertical-align:middle;
white-space:nowrap;
}

.yt-uix-button:hover, .yt-uix-button:focus {
-moz-box-shadow:0 0 3px #FF0000 !important;
background:-moz-linear-gradient(center top , #FFEEEE, #FFBBBB) repeat scroll 0 0 #FF0000 !important;
border-color:red !important;
outline:0 none;
}

}
