css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com") {
*
{
	font-family: Helvetica, Arial, sans-serif !important;
}
#styleguide-v2, #styleguide-v2 #wrapper
{
	background-image: none !important;
}
A
{
	text-decoration: none !important;
}
A:hover
{
	text-decoration: underline !important;
}
H1.header
{
	font-size: 30px !important;
}
HR
{
	border-style: solid !important;
	border-color: #FFF !important;
}
#content-2-wide .article:first-child
{
	border: 0 !important;
	padding: 0 !important;
}
.article
{
	border: 0 !important;
	-moz-border-radius: 0 !important;
	-webkit-border-radius: 0 !important;
	-khtml-border-radius: 0 !important;
	border-radius: 0 0 0 0 !important;
	padding: 10px 0 !important;
}
.article H2
{
	margin: -9px 0px 15px !important;
	font-size: 14px !important;
	letter-spacing: 0.02em !important;
	text-rendering: optimizeLegibility !important;
	text-transform: uppercase !important;
	padding: 6px 10px !important;
	color: #FFF !important;
	background: #444 !important;
}
.article H2 A
{
	color: #EC0 !important;
}
.article.highlighted
{
	border: 0 !important;
	margin: 0 !important;
	padding: 10px !important;
}
.rightcornerlink
{
	background: #FFF !important;
	padding-bottom: 8px !important;
	border: 0 !important;
}
.redesign .aux-content-widget-2, .redesign .aux-content-widget-3, .article.on-tv
{
	border: 0 !important;
	-moz-border-radius: 0 !important;
	-webkit-border-radius: 0 !important;
	-khtml-border-radius: 0 !important;
	border-radius: 0 !important;
	-moz-box-shadow: none !important;
	-webkit-box-shadow: none !important;
	-o-box-shadow: none !important;
	box-shadow: none !important;
	padding: 20px !important;
}
.aux-content-widget-5 .contextual
{
	display: none !important;
}
.news_item
{
	padding: 10px 0 !important;
	border: 0 !important;
	background: none !important;
}
.star-box
{
	border: 0 !important;
	background: none !important;
	padding: 0px 0px 10px !important;
}
.see-more A
{
	font-style: italic !important;
}
.top-rhs
{
	display: none !important;
}
.social, .social_networking
{
	border: 0 !important;
	padding: 0 !important;
}
.social_networking IFRAME
{
	height: 25px !important;
}
}
