css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org")
{
	html
	{
		background: #101010 !important;
	}
	body
	{
		background: url("http://img811.imageshack.us/img811/2827/18555723.png") !important;
		color: #eee !important;
		font: 12px Calibri !important;
		margin: 0 auto !important;
		padding: 0;
	}
	form
	{
		background: #101010;
		border-left: 1px solid #222;
		border-right: 1px solid #222;
		margin: 0 auto !important;
		max-width: 900px !important;
		padding: 25px 5px 5px 5px;
		width: 85% !important;
	}
	#recent-images li
	{
		background: #1c1c1c !important;
	}
	#header
	{
		width: 100% !important;
	}
	#navtop
	{
		background: #101010 !important;
		border-bottom: 1px solid #1c1c1c;
		color: #8899AA !important;
		font: 12px Calibri !important;
		padding: 0;
		padding: 5px !important;
		position: fixed;
		text-align: center !important;
		top: 0;
		width: 100% !important;
		z-index: 9;
	}
	div.logo
	{
		left: -700px;
		position: absolute;
		right: 0;
		visibility: hidden;
	}
	div.logo font span
	{
		color: white;
		font: 12px Calibri !important;
		visibility: visible;
	}
	center font[color="red"]
	{
		display: none !important;
	}
	TABLE[align="right"]
	{
		display: none !important;
	}
	BODY TABLE[width="100%"]
	{
		margin-bottom: -1px;
		padding: 0 !important;
		padding-left: 10px !important;
		padding-right: 10px !important;
	}
	BODY TABLE[width="100%"] TH
	{
		padding: 8px !important;
	}
	.postarea
	{
		padding: 0 !important;
	}
	.postarea form div
	{
		border: none !important;
	}
	.postarea input
	{
	}
	.postarea form
	{
		border-bottom: 1px solid #212121;
		padding-bottom: 18px !important;
	}
	.postarea table
	{
		margin-bottom: -10px;
		margin-top: -10px;
	}
	.postblock
	{
		background: #1c1c1c !important;
		border: 0 !important;
		color: #aaa !important;
	}
	input, input[type="submit"], input[type="button"], select, button, textarea
	{
		background: #000 !important;
		border: 1px #333 solid !important;
		color: #aaa !important;
		moz-appearance: none;
	}
	input[type="checkbox"]
	{
		background: rgba(0,0,0,.4) !important;
		border: 1px solid rgba(255,255,255,.3) !important;
		border-radius: 2px !important;
		height: 10px !important;
		moz-appearance: none !important;
		moz-border-radius: 2px !important;
		webkit-appearance: none !important;
		webkit-border-radius: 2px !important;
		width: 10px !important;
	}
	input[type="checkbox"]:checked
	{
		background: rgba(255,255,255,.4) !important;
	}
	img#recaptcha_reload
	{
		background-image: url(data:image/png;base64,) !important;
		border: none !important;
		height: 0px !important;
		margin: 0 !important;
		padding-top: 18px !important;
		width: 25px !important;
	}
	img#recaptcha_switch_audio
	{
		background-image: url(data:image/png;base64,) !important;
		border: none !important;
		height: 0px !important;
		margin: 0 !important;
		padding-top: 15px !important;
		width: 25px !important;
	}
	img#recaptcha_whatsthis
	{
		background-image: url(data:image/png;base64,) !important;
		border: none !important;
		height: 0px !important;
		margin: 0 !important;
		padding-top: 16px !important;
		width: 25px !important;
	}
	#recaptcha_logo, #recaptcha_tagline
	{
		display: none !important;
	}
	a
	{
		border: 0 !important;
		color: #ffffff !important;
		text-decoration: none !important;
	}
	a:hover
	{
		background: #222 !important;
		color: #bb0000 !important;
	}
	th
	{
		background: #1c1c1c !important;
		padding: 10px !important;
	}
	td.reply
	{
		background-color: #1c1c1c !important;
		border: 0 !important;
		clear: both;
		color: #FFF !important;
		width: 900px !important;
	}
	td.replyhl
	{
		background-color: #141414 !important;
		border: 0 !important;
		color: #FFF !important;
		width: 900px !important;
	}
	.pages
	{
		background: #222 !important;
		border: 0 !important;
		text-align: center !important;
		width: 100% !important;
	}
	.pages td, .pages td b
	{
		color: #666 !important;
		font: 12px Calibri !important;
	}
	.postername, .abbr
	{
		color: #999999 !important;
	}
	.commentpostername
	{
		color: #999999 !important;
	}
	.postertrip
	{
		color: #337733 !important;
	}
	.filetitle
	{
		color: #6666ff !important;
	}
	a img
	{
		border: 1px solid #AAA !important;
		clear: both !important;
		margin: 0 10px 10px 2px !important;
	}
	.deletebuttons input:not([type="checkbox"])
	{
		display: inline !important;
		width: 130px !important;
	}
	.deletebuttons input[type="checkbox"]
	{
		position: relative;
		top: 3px;
	}
	.quotejs
	{
		visibility: visible;
	}
	.replytitle
	{
		color: #6666ff !important;
	}
	.deletebuttons
	{
		visibility: hidden;
	}
	body form
	{
		font: 12px Calibri !important;
	}
	td
	{
		font: 12px Calibri !important;
	}
	.omittedposts
	{
		color: #777 !important;
		margin-left: 10px;
		visibility: visible !important;
	}
	.filesize
	{
		display: inline-block;
		padding-bottom: 6px;
		padding-left: 2px;
		padding-top: 3px;
	}
	body form blockquote
	{
		background: #1c1c1c;
		margin: 8px 2px 8px 20px;
		padding: 10px;
	}
	td blockquote
	{
		background: none !important;
		margin: 0 !important;
		padding: 5px !important;
	}
	form td
	{
		padding: 2px 6px !important;
	}
	#navtopr, #hd, #ft, hr, td small, .doubledash, #footer, .rules, center font h3, center font small, center small font
	{
		display: none !important;
	}
	img[class*="4chan_ext"]
	{
		border: 0 !important;
		display: inline;
		margin: 0 !important;
	}
	div[id*="twi_table"]
	{
	}
	.reply
	{
		background: #1C1C1C !important;
		border: 1px solid #444 !important;
		color: #EEE !important;
		font: 12px Calibri !important;
	}
	.postername , .postertrip, .filetitle, .commentpostername
	{
		font: 12px Calibri !important;
	}
	.reply[id*="ext"]
	{
		padding: 1px !important;
	}
	div[id*="twi_table"] .postblock
	{
		font: 12px Calibri !important;
		padding: 3px !important;
	}
	form[id*="4chan_qr_form"]
	{
		padding: 5px;
		width: auto !important;
	}
	span[class*="4chan_win_header_buttons"] img
	{
		display: inline;
		padding: 1px;
	}
	img
	{
		display: block;
		margin: 0 auto;
	}
	img[src*="http://static.4chan.org/"]
	{
		display: none !important;
	}
        #backwash_tooltip
        {
        	background-color: #1c1c1c !important;
        }
}
@-moz-document url(http://www.4chan.org/)
{
	HTML > BODY
	{
		color: #dddddd;
		padding: 10px 0px !important;
	}
	#hd, #close-announce
	{
		background: #222222 !important;
		color: #dddddd;
		display: none !important;
	}
	.boxbar, #preview-tooltip-nws H2, #preview-tooltip-ws H2
	{
		background: #222222 !important;
		border-bottom: 1px solid rgba(0,0,0,.2) !important;
		color: #bb0000 !important;
		color: #dddddd;
		text-align: center !important;
	}
	#announce .boxcontent
	{
		background: #222222 !important;
		color: #dddddd;
		text-align: center !important;
	}
	.box-outer, #preview-tooltip-nws, #preview-tooltip-ws
	{
		background: #222222 !important;
		border-width: 12px !important;
		color: #dddddd;
	}
	.menubutton
	{
		background: #222222 !important;
		background-image: none !important;
		color: #dddddd;
	}
	#filter-button, #option-button
	{
		letter-spacing: 0px !important;
		margin: 0 !important;
		padding: 0px !important;
	}
	#filter-button:hover, #option-button:hover
	{
		opacity: .7 !important;
	}
	.yui-menu-shadow
	{
		display: none !important;
	}
	#recent-images LI
	{
		background: #222222 !important;
		background: none !important;
		color: #dddddd;
	}
	#boards .column
	{
		background: #222222 !important;
		color: #dddddd;
		text-transform: lowercase !important;
		width: 12em !important;
	}
	.column > H3, .p_postername
	{
		color: #bb0000 !important;
	}
	.box-outer.right-box, .box-outer.left-box,.box-outer.top-box
	{
		background: #222222 !important;
		color: #dddddd;
	}
	.box-outer, #preview-tooltip-nws, #preview-tooltip-ws
	{
		background: #222222 !important;
		border: 5px solid #101010 !important;
		color: #dddddd;
	}
	#preview-tooltip-nws, #preview-tooltip-ws
	{
		background: #222222 !important;
		color: #dddddd;
		padding: 5px !important;
	}
	.boxcontent img
	{
		background: #222222 !important;
		color: #dddddd;
		margin: 0 auto !important;
	}
}
@-moz-document url-prefix(http://www.4chan.org/framesnav)
{
	BODY
	{
		font: 11px verdana !important;
	}
	#logo
	{
		background: none !important;
		height: 0 !important;
		padding-bottom: 5px !important;
	}
	.plus
	{
		background: #DDD !important;
		border: none !important;
	}
	H2
	{
		background: #1C1C1C !important;
		color: #FFF !important;
	}
	LI:hover
	{
		text-decoration: none !important;
	}
	li
	{
		color: #6666ff !important;
		padding-left: 5px !important;
		padding-right: 5px !important;
	}
}
