css:
/* Twitter in the Sidebar for #NewTwitter EXPERIMENTAL | 20110612 | nukumori.org */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(https://twitter.com/?sidebar), url-prefix(http://twitter.com/?sidebar){

/* header === */
.banner,
.banner .banner-inside,
#top-bar,
#top-bar .top-bar-inside{
max-width:100% !important;
min-width:0 !important;
margin:0 !important;
padding:0 !important;
}

.welcome-banner-inside span,
.banner-inside .first-banner-row,
.banner-inside .second-banner-row {
padding:0 !important;
}

#top-stuff{
opacity:.5 !important;
height:40px !important;
overflow:hidden !important;
display:none;
}
#doc:hover #top-stuff{
display:block;
}
#top-stuff:hover{
opacity:1 !important;
height:auto !important;
overflow:visible !important;
}

#top-bar-outer {
height:auto !important;
}

#global-nav {
width: 100%;
}
.top-bar-inside:after{
display:block;
content:"";
clear:both;
}

.main-tweet-box {
padding:0 !important;
}

.stream-tabs .tab-text {
line-height:1 !important;
}

/* body === */
#page-outer,
#page-container{
max-width:100% !important;
min-width:0 !important;
margin:0 !important;
padding:0 !important;
position:relative !important;
}
.main-content{
width:100% !important;
}
.home-header{
padding-top:40px !important;
}
.stream-tabs{
display:inline-block !important;
height:auto !important;
padding:0 !important;
}

/* notification */
.stream .new-tweets-bar {
-moz-border-radius:0 !important;
bottom:0 !important;
margin:0 !important;
padding:0 !important;
position:fixed !important;
width:100%;
z-index:1;
border-width:1px 0 0 0 !important;
}

.stream .new-tweets-bar:hover {
padding:.5em 0 !important;
}

/* dialog */
.twttr-dialog{
max-width:90% !important;
}
.twttr-dialog-content{
width:100%;
}
.twttr-dialog .twitter-anywhere-tweet-box-editor{
width:95% !important;
max-width:95% !important;
}
.twitter-anywhere-tweet-box-editor{
max-width:90% !important;
}
.twttr-dialog-footer{
width:auto !important;
}
.twttr-dialog-reply-footer p{
top:0 !important;
}

/* tweets */
.stream-item, .unselectable-stream-item{
min-height:0 !important;
}
.tweet.stream-tweet {
font-size:9pt !important;
padding:0 !important;
}
.tweet{
padding-right:0 !important;
}

.tweet-content {
margin-left:0 !important;
min-height:0 !important;
}
.tweet-image{
width:1em !important;
height:1em !important;
margin:0 !important;
margin-right:3px !important;
position:relative !important;
overflow:visible !important;
z-index:1;
}
.tweet-image .user-profile-link{
position:absolute !important;
width:1em !important;
height:1em !important;
top:1px !important;
left:1px !important;
}
.tweet-image:hover .user-profile-link{
position:absolute !important;
width:48px !important;
height:48px !important;
background:#fff !important;
-moz-box-shadow:0 0 10px rgba(0,0,0,.7);
}
.extra-icons{
right:18px !important;
}
.stream-item .more{
margin-top:0 !important;
right:0 !important;
}

/* sidebar */
#details-pane-outer{
z-index:5 !important;
}

.simple-tweet-content,
.details-pane .tweet-text{
font-size:9pt !important;
}
.details-pane .inner-pane{
margin-left:0 !important;
}
.details-pane .tweet-text-large{
font-size:12pt !important;
line-height:1.2 !important;
}
.mentioned-in-tweet h3,
.related-tweets h3{
padding:0 !important;
font-size:12pt !important;
line-height:1.2 !important;
}
.details-pane{
position:fixed !important;
left:5% !important;
right:5% !important;
top:50px !important;
bottom:0 !important;
width:auto !important;
min-width:0 !important;
max-width:100% !important;
z-index:10000000 !important;
-moz-box-shadow:0 0 20px rgba(0,0,0,.9) !important;
-moz-border-radius:5px !important;
}
.pane-toolbar {
-moz-border-radius:5px 5px 0 0 !important;
}
.pane-components{
-moz-border-radius:5px !important;
}
.details-pane-outer{
left:auto !important;
z-index:5 !important;
width:90% !important;
}
.details-pane-shell{
min-width:0 !important;
}

/* Drect Messages */
.route-messages .details-pane{
left:120px !important;
right:5px !important;
}
.route-messages .messages-pane{
padding-left:5px !important;
padding-right:0 !important;
}
.route-messages .message{
padding-right:0 !important;
}
.route-messages .user-name,
.route-messages .message-content .linked-text{
font-size:9px !important;
line-height:1.25 !important;
}

}
