css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ogame.com.es") {

/* @@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@ */
/* Atributos base
X[class="X"]
color: #FFFFFF !important;
font-size: 11px !important;
text-decoration: none !important;
font-weight: bold !important;
*/












/* --------- GENERAL --------- */

#playerName span.textBeefy {color: #FFFFFF !important;}

#errorBoxDecision h4#errorBoxDecisionHead
{color: #FFFF00 !important; font-size: 20px !important; text-decoration: blink;}
#errorBoxDecision p#errorBoxDecisionContent
{color: #FFFFFF !important; font-size: 14px !important;}

/* RECURSOS */
#resources span#resources_metal
{color: #AAAAAA; font-size: 10px !important;}
#resources span#resources_crystal
{color: #9999FF;font-size: 10px !important;}
#resources span#resources_deuterium
{color: #00CC00;font-size: 10px !important;}
#resources span#resources_energy
{color: #FFFFFF;font-size: 10px !important;}
#resources span#resources_darkmatter
{color: #CC55CC;font-size: 10px !important;}
#resources  span.overmark
{color: #FF0000 !important; font-weight: bold !important;}

#changelog_link {font-size: 0px !important; border: 0px !important;}


/* Script Recursos en vuelo */
#eventContent #resourcesInFlight {margin-left: 40px !important;}
#resourcesInFlight {padding-bottom: 15px; padding-top: 15px; text-align: left !important; margin-left: 6px !important;}

/* Script Antigame */
.antires span.undermark {color: #00FF00 !important; font-weight: bold !important;}
.antires span.finishtime {color: #FF0000 !important;}
.antires b  {color: #9999FF !important; font-weight: bold !important; font-size: 12px !important;}





/* --------- VISION GENERAL --------- */

/* EVENT LIST SUPERIOR */
#eventtype span#eventFriendly {font-weight: bold !important;}
#eventtype span#eventNeutral {font-weight: bold !important;}
#eventtype span#eventHostile {font-weight: bold !important;}
#eventNeutral {color: #9999FF;}
td[class="neutral col2"] {color: #9999FF !important;}
 

/* INFO PLANETA */
#planetDetails td.data #positionContentField a {color: #ffffff !important;}
#planetDetails td.data #positionContentField a:hover {color: #9999FF !important; text-decoration: none !important;}
#planetDetails td.data #scoreContentField a {color: #ffffff !important;}
#planetDetails td.data #scoreContentField a:hover {color: #FFA000 !important; text-decoration: none !important;}

/* ACTUALES */
table.construction span.level {color: #00FF00 !important; font-weight: bold !important;}
table.construction span#Countdown {color: #00bb00 !important;}
table.construction span#researchCountdown {color: #00bb00 !important;}
table.construction td.finishTime {color: #777777 !important;}
table.construction td.idle {font-size: 0px !important;}






/* --------- MOVIMIENTO FLOTA --------- */

/* MISIONES MOV. FLOTA */

/* Misiones de ataque */
.fleetDetails .hostile {color: #FF0000 !important; font-size: 11px !important; text-decoration: blink;}
/* Amistosas, propias, retorno */
.fleetDetails .friendly {color: #ffffff !important; font-size: 11px !important;}
/* No ataques */
.fleetDetails .neutral {color: #9999ff !important; font-size: 11px !important;}

.targetName {color: #ffffff !important;} /* Nick jugador */

.timer {color: #00FF00 !important; font-weight: bold !important;} /* Cuenta atras 1 */
.absTime {color: #AAAAAA !important;} /* Hora de llegada */
.nextTimer {color: #00FF00 !important; font-weight: bold !important;} /* Cuenta atras 2 */
.nextabsTime {color: #AAAAAA !important;} /* Hora de retorno */
.reversalTime {color: #FFA000 !important;}

.originPlanet {color: #9999ff !important;} /* Planeta de origen */
.destinationPlanet {color: #ff5555 !important;} /* Planeta destino */

.fleetStatus span {color: #aaaaaa !important;} /* Titulos */
.fleetStatus span.current {color: #ff0000 !important;} /* Huecos utilizados */
.fleetStatus span.all {color: #00ff00 !important;} /* Huecos maximos */







/* --------- GALAXIA --------- */

#galaxyContent #probeValue {color: #FF00FF !important; font-weight: bold !important;}
#galaxyContent #recyclerValue {color: #e0830a !important; font-weight: bold !important;}
#galaxyContent #missileValue {color: #FF0000 !important; font-weight: bold !important;}
#galaxyContent #slotValue {color: #999999 !important; font-weight: bold !important;}
#galaxyContent #slotValue #slotUsed {color: #FFFFFF !important; font-weight: bold !important;}



/* GMARK - Marcador de Galaxia */
span#changeCase input[type="button"] {display:none !important;}
span#changeCase {color: #9999FF !important; font-weight: none !important;}
span#changeCase:hover {color: #FFF000 !important; font-weight: none !important;}
span.marqueur input[type="button"]
{font-size: 9px !important; border-color:#22313b !important; margin-left: 1px !important;}


/* Colores Galaxia */
td.planetname {color: #AAAAAA !important; font-size: 10px !important;}
td.allytag {color: #AAAAAA !important; font-size: 10px !important;}
span.status_abbr_active {color: #dddddd !important; font-size: 11px !important;}
span.status_abbr_inactive {color: #FFFF00 !important; font-size: 12px !important;font-weight: bold !important;}
span.status_abbr_longinactive {color: #FF8000 !important; font-size: 12px !important;font-weight: bold !important;}
span.status_abbr_vacation {color: #0080FF !important; font-size: 10px !important;}
span.status_abbr_strong {color: #FF0500 !important; font-size: 10px !important;}
span.status_abbr_noob {color: #009900 !important; font-size: 10px !important;}
/*span.anti_rank {color: #FF0000 !important; font-size: 10px !important;}*/
/*span.anti_allyrank {color: #AAAAAA !important; font-size: 10px !important;}*/
td.planetname span.undermark {color: #FF3333 !important; font-size: 11px !important;font-weight: bold !important; text-decoration: blink;}
div.anti_debris {color: #FFFF00 !important; font-size: 11px !important;font-weight: bold !important;}





/* --------- MENSAJES --------- */

/* Coordenadas en mensajes esp/bat */
div.note Font[color="red"] 
{font-weight: bold !important; text-decoration: blink !important;}
div.note a 
{color: #DDA000 !important; font-size: 12px !important; font-weight: bold !important;}
div.note a:hover 
{color: #FFFF00 !important; font-size: 12px !important; text-decoration: none !important; font-weight: bold !important;}






/* @@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@ */
}
