css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {

body, #Photo p, #Photo .TagList, #Photo #otherContexts_div, #Photo #button_bar, #Photo .Stats, .Photostream, .SetsColumn, .For div
         { background-color: gray !important; color: black !important;
           font-family: Verdana, sans-serif !important; }

#Photo   { margin-top: 25px !important; }

#AdBlock, #NotSearchAdBlock, .Ad, .Spiel, #beacon, #homepage_ad, .UpgradeBox, .HomeBox, .LeftCol a img
         { display: none !important; }

#Photo a:hover, .SetsColumn a:hover, #Subnav a:hover, #Hint a:hover, .DiscussionResults a:hover, .ResultSet a:hover, .StreamView a:hover, #Viewset a:hover, .RightCol a:hover, #ProfileInfo a:hover, .Footer a:hover, .Infocase a:hover
         { color: white !important;
           background-color: inherit !important;
           text-decoration: underline !important; }

#Photo a:visited, .SetsColumn a:visited, #Subnav a:visited, #Hint a:visited, .DiscussionResults a:visited, .ResultSet a:visited, .StreamView a:visited, #Viewset a:visited, .RightCol a:visited, #ProfileInfo a:visited, .Footer a:visited, .Infocase a:visited
         { color: #ddd !important;
           background-color: inherit !important;
           text-decoration: underline !important; }

#Photo a:link, #Photo .Plain:link, .SetsColumn a:link, #Subnav a:link, #Hint a:link, #DiscussionResults a:link, .ResultSet a:link, .StreamView a:link, #Viewset a:link, .RightCol a:link, #ProfileInfo a:link, .Footer a:link, .Infocase a:link
          {  color: white !important;
             background-color: inherit !important;
             text-decoration: underline !important; }

.SetsColumn p, .Glance li, .StreamView p, #Viewset p
          { color: black !important; }

.site_nav_menu_buttons a:hover
          { color: black !important;
            background-color: inherit !important;
            text-decoration: underline !important; }

.site_nav_menu_buttons a:visited
          { color: black !important;
            background-color: inherit !important;
            text-decoration: underline !important; }

.site_nav_menu_buttons a:link, .site_nav_menu_buttons .Plain:link
          { color: black !important;
            background-color: inherit !important;
            text-decoration: underline !important; }


#Photo #DiscussPhoto p small, .launchHead small
          { color: #bbb !important; }

#Photo #DiscussPhoto h3, #Photo .Taglist h4, #Photo .RHS h4, #Photo .Widget, #Photo #other_contexts_p, #Left h3, #Right h3, .Jump th 
          { color: white !important; }

#Photo .Said p, div div h1, div div h3, div table h3
          { color: white !important; }

#photoswftd h1, .photoDescription
          { color: white !important;
            font-size: larger !important; }

.site_nav_menu_buttons, .site_nav_menu_buttons li, .site_nav_menu_buttons li.menu_li
          { border-width: 0px !important;
            border-style: none !important;
            border-color: gray !important;
            font-size: smaller !important; }

#GoodStuff td
          { background-color: #ccc !important; }

.Status
          { color: white !important; }

.Status a
          { color: white !important;
            text-decoration: underline !important; }

#Photo #otherContexts_div a, #thetags a
          { font-size: smaller !important; }
}
