@-moz-document domain("browserjs.blog102.fc2.com") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#all
, #all div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#all * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#all a:link { color : #0000ee !important; }
#all a:visited { color : #551a8b !important; }
#all a:hover { color : #ee0000 !important; }
#all a:hover { background-color : transparent !important; }
/* /destroy div layout */

h2
, #right
, #right2
, h1 table tbody tr td a img
, h1 table tbody tr td + td
, hr
, div#footer
, div[align="center"]
, div[align="right"]
, div[align="right"] + br + ul
, a[href="http://www.trackword.net/"]
, img[alt="[Image]"]
, form[name="comment_form"] + a
, img[alt*="å¹ãå‡ºã—"]
, br[clear="all"]
, li.PageEdit
, .sub li img
, .pgselec_all
, a[onclick]
, div[id^="delicious-"]
, div.fc2_footer
, img[src$="New.png"]
, h3 a img
, ul li a img[src^="http://b."]
, ul li a img[src^="http://image.clip"]
, h5 + a
, h5 + a + br
{ display : none !important; }

h1
, h3
, h5
{ padding : 0 !important; }

h3
, h5
{ border : 0 !important; }

#all h3
{ margin-bottom : 1em !important; }

#all h5
{ margin-top : 1em !important; }

h1 table tbody tr td a:before {
content : "Browser.js" !important;
}

