css:
#contentCol  { background-color:#FBFBFB!important; }

.profile .profile_color_bar  { background: #FFFFFF url(http://semilla.host22.com/facebook/smooth.png) top left repeat-x !important; }







#blueBar       { background:#000000 url(http://semilla.host22.com/facebook/bluebar90.png) bottom left no-repeat !important; }

#pageLogo a    { background:url(http://semilla.host22.com/facebook/logo_icons90.png) no-repeat scroll -21px 0 !important; }

#headNavOut    { background:#ffffff url(http://semilla.host22.com/facebook/3dsearch90.png) center right no-repeat !important; border-color:#000000!important; }

	







.jewelToggler  { background-image:url(http://a.imageshack.us/img801/3807/46nnp.png) !important; }

#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,

#jewelMail:hover, #jewelMail:focus, #jewelMail:active,

#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color:#bbbbbb !important; }

#jewelCase .jewel  { border:none !important; }









#navSearch button  { background:url(http://semilla.host22.com/facebook/play.png) no-repeat scroll -1px 0 !important; }

#navSearch     { border-color:#868686 !important; }









#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#000000 !important; color:#ffffff !important; }









.lightblue_box  { background-color:#eeeeee !important; border-color:#bbbbbb !important; }

.commentable_item .ufi_section  { background-color:#eeeeee !important; }

textarea, .inputtext, .inputpassword  { border-color:#bbbbbb !important; }









a  { color:#000000 !important; }

.uiLinkButton input  { color:#2c2c2c !important; }

.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#2c2c2c !important; }

button.as_link  { color:#2c2c2c !important; }









.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#bbbbbb !important; }



.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#eeeeee !important; }

.friends_online_sidebar li.buddy_row a:hover  { background-color:#eeeeee !important; }



.unread  { background-color:#bbbbbb !important; }

.line  { color:#000000!important; }









#jewelCase .jewelFooter a   { background-color:#eeeeee !important; }

#jewelCase .jewelFooter .uiTextSubtitle  { color:#000000 !important;  }



#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#000000 !important; }



#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#eeeeee !important; border-color:#868686 !important; }

#jewelCase .jewelBox  { background-color:#fbfbfb !important; }









.uiHeader h2  { color:#444444 !important; }









.current_stuff td  { background-color:#fbfbfb !important; border-color:#bbbbbb !important; }









.UIComposer .UIComposer_Box  { background-color:#fbfbfb !important; border-color:#bbbbbb !important; }

.profile .box .box_header  { background-color:#fbfbfb !important; border-color:#bbbbbb !important; }









.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#bbbbbb !important; }

.profile .top_bar ul.tabs li a.tab_link  { background-color:#bbbbbb !important; border-color:#868686 !important; }

.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#444444 !important; color:#ffffff !important; border-color:#444444 !important; }











#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#fbfbfb !important;  }

#chat_tab_bar .chat_header  { background-color:#868686 !important; border-color:#000000 !important; }

#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }

#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#bbbbbb !important; color:#444444 !important; }

#presence_bar .presence_menu_opts h2 span  { background:#868686 !important; }

#presence_bar .presence_menu_opts h2  { border-color:#868686 !important; }

.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#fbfbfb !important; }



#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#868686 !important; border-color:#868686 !important; }

#presence .presence_bar_button .inner_button  { border-top:none !important; }

#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }



#presence .presence_menu_opts  { border-color:#868686 !important; }









.UIButton_Blue  { background:#444444 !important; border-color:#868686 !important; }

.UIComposer_InputArea  { border-color:#bbbbbb !important; }

.uiButtonConfirm  { background:#000000 !important; border-color:#868686 !important; }

#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }
