css:
@-moz-document url-prefix("http://www.poneyvallee.com/forum"){

/* Fond du forum */
Body {
	Background-image : url("http://img689.imageshack.us/img689/4972/alto3.jpg") !important;
	Background-position : center !important;
	Background-attachment : fixed !important;
        Background-repeat : repeat !important;
        Background-size: 100% !important;}

#bg td{background-image: none !important;}
.right{border-style: none !important;}
.left{border-style: none !important;}
.center{border-style: none !important;}

head{background : none !important;}

.pun {
  color: rgb(255,128,0) !important;
color : DarkOrange !important;
margin-left : -25% !important;
margin-right : -25% !important;
}

/* Couleur texte et lien des menus */
#brdmenu{background-color: rgba(0,0,0,0.8) !important;}
#brdmenu a:link { color: DarkOrange !important;}
#brdmenu a:visited { color: DarkOrange !important;}
#brdmenu a:hover { color: yellow !important;}
#brdmenu a:active { color: yellow !important;}

#announce a:link{color : black !important;}
#announce a:visited{color : black !important;}
#announce a:hover{color : gray !important;}
#announce a:active{color : gray !important;}

/* Les deux premiers cadres, colonnes de l'index sauf "messages" et "discussion"*/
DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven,#postreview {
      background-color : rgba(0,0,0,0.8) !important;
}

/* colonnes "messages" et "discussion" */
TD.tc2, TD.tc3, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo,
#adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD {background : transparent !important;
}

/* Barres de titres de chaque partie */
.pun H2{background-color: rgba(0,0,0,0.8) !important;
        color : DarkOrange !important;
}

/* Barres sous les titres de chaque partie  */
.pun TH{background-color : rgba(0,0,0,1) !important;
                color : yellow !important;
}
.linkst,.linksb,.postlinksb{background-color: rgba(0,0,0,0.3) !important;}

/* post, colonne gauche hors avatar et cadre à avatar */
DIV.blockpost DIV.box, DIV.postright, DIV.postfootright {background : transparent !important;}
DIV.postright, DIV.postfootright {BORDER-LEFT-COLOR: transparent !important;}
DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {background : transparent !important;}

/* fond de la barre à date de chaque post et couleur du #numéro */
DIV.blockpost H2 SPAN.conr {COLOR: white !important;}
.blockpost{border: 1px ridge rgba(255,255,0,1) !important;
	padding: 5px !important;}

td{border: 1px ridge rgba(255,255,0,0.4) !important;
	padding: 5px !important;}

a:link { color: rgb(255,0,0) !important;} /* lien non-visité */
a:visited { color: rgb(255,0,0) !important;} /* lien visité */
a:hover { color: yellow !important;} /* lien survolé */
a:active { color: yellow !important;} /* lien activé */

/* cadre des messages avec les smileys */
#message {background-color : rgba(255,255,255,1) !important;}
div.txtarea{width: 670px !important;
            height: 280px !important;
            margin-left: 0px !important;
}
div.inform{margin-top: 0px !important;}
#smileys{height: 130px !important;}
.box input[name="submit"]{margin-left: 340px !important;}
div.txtarea textarea{height: 90px !important;}

.pmlink a:link{color:rgb(255,0,0)  !important;}
.pmlink a:visited{color:rgb(255,0,0)  !important;
   text-decoration : blink !important;}

/* barre tout en haut des pubs et carde pub apres premier message de la page */
div[style="position:fixed; z-index:10000; width:100%; height:24px; left:0px; top:0px; background-color:#494949; overflow:hidden"] {background-color: transparent !important;
                               background-image:none !important;
            display : none !important;}
div[style="height: 100%; width: 300px; margin-left: 5px;"]{display: none !important;}

div.icon{background-image: url("http://img146.imageshack.us/img146/3473/altou.gif") !important;
 background-size : 110% !important;
  padding-left: 5px !important;
  margin-right: 5px !important;
   margin-left: -3px !important;}
div.inew{background-image: url("http://img715.imageshack.us/img715/3295/saxophone.gif") !important;
 background-size : 110% !important;
    margin-right: 5px !important;
    padding-left: 5px !important;}

.iclosed .icon{background-image: url("http://img847.imageshack.us/img847/2091/timbale2.gif") !important;
 background-size : 80% !important;
 margin-left: 3px !important;
 margin-right: -3px !important;}
div.tclcon{margin-left: 35px !important;}

}
