css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.poneyvallee.com/forum/") {

/*Dédicacé à Kenose, puisqu'elle voulait ce thème ;-p */

body {
	background : #44C9F6 url("http://nsm08.casimages.com/img/2014/08/27/14082712114410339312480725.jpg") center repeat !important;
	color : #444444 !important;
}


#brdheader {
	background: white !important;
	border : 2px #FCBC85 solid !important;
	margin-top:5px !important;
}

img[src*="/images/accueil/pv_titre.png"] {
	background-image: url("http://nsm08.casimages.com/img/2014/10/16//14101601595210339312617108.png");
	width: 0 !important;
	height: 109px !important;
	padding-left: 495px !important;
}

#brdheader .box{
	background: transparent !important;
	border : none !important;
}
#brdmenu {
	background: transparent !important;
}



h2 {
	background: #6FC055 !important;
	color : white !important ;
	border : 2px #6FC055 solid !important;
	border-bottom : none !important ;
}

thead th, .arrondis_bas {
	background: white !important;

}
.arrondis_bas {
	border : 2px #6FC055 solid !important;
}

tbody td, thead th {
	background: transparent !important;
	border : none !important ;
	border-top : 1px solid #6FC055 !important ;
}
tbody h3 a {
	border-bottom : none !important ;
}


.icon.inew {
	background:transparent left no-repeat !important;
	background-size: 27px 25px !important; 
  opacity:1;
}
.icon {
	background:transparent no-repeat !important;
	background-size: 27px 25px !important; 
        opacity:0.5;
}


.box, .blockpost, .inbox {
	background : transparent none !important;
}

.box{
	background : white none !important;
}

DIV.postright {
	BORDER-LEFT: solid 198px #BAD976 !important;
}
.box dl, .postright {
	background: white !important;
}

.box dl, .postleft, .postfootleft {
	background:  #BAD976 !important;
}
.postfootleft {
	border-radius : 0px 0px 15px 15px !important ;
}
.postfootright {
	background: white !important;
}
.postfootright {
	border-radius : 0px 0px 15px 15px !important ;
}
blockquote, .incqbox {
	background:  #E8EC99 !important;
}

h2 span a {
	color:  white !important ;
	border-bottom :  white 2px dotted !important ;
}
h2 span a:hover {
	border-bottom :  white 1px dashed !important ;
}

#brdmenu a{
	color : #FCBA63 !important;
	border-bottom: #FECC4E 1px solid !important;
}
#brdmenu a:hover{
	color : #FECC4E !important;
	border-bottom: #FFE5B8 dotted 2px !important;
}
#brdwelcome li {
	color : #FECC4E !important;
}
#brdwelcome li a{
	color : #C8AD97 !important;
	border-bottom : 1px dotted #A88B76 !important;
}



h2 {
	border-radius : 15px 15px 0px 0px !important ;


}
.box {
	border-radius : 0px 0px 15px 15px !important ;
	border : 1px dotted #6FC055 !important;
	border-top : none !important;
}

}

@-moz-document url-prefix("http://www.poneyvallee.com/forum/index.php") {

h2 {
	background: #FCBC85 repeat fixed !important;
	color : white !important ;
	border : 2px #FCBC85 solid !important;
	border-bottom : none !important ;
}

thead th, .arrondis_bas {
	background: white !important;

}
.arrondis_bas {
	border : 2px #FCBC85 solid !important;
}

tbody td, thead th {
	background: transparent !important;
	border : none !important ;
	border-top : 1px solid #FCBC85 !important ;
}
tbody h3 a {
	border-bottom : none !important ;
}

}
