css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://"), url-prefix("https://") {

div.autoPagerS {
    background: url(data:image/png;base64,) 50% repeat-x !important;
    border: none !important;
    height: 18px !important;
    margin: 4px !important;
    padding: 0 !important;
    position: relative !important;
}

div.autoPagerS span {
    visibility: hidden !important;
}

div.autoPagerS a {
    position: absolute !important;
    text-decoration: none !important;
    visibility: visible !important;
}

div.autoPagerS a img {
    position: absolute !important;
    -moz-transition: bottom    0.5s ease-in-out, top 0.5s ease-in-out !important;
    -webkit-transition: bottom 0.5s ease-in-out, top 0.5s ease-in-out !important;
    transition: bottom         0.5s ease-in-out, top 0.5s ease-in-out !important;
}

div.autoPagerS span a:nth-of-type(2)         {  left:  0px !important;    top: 0px !important; }
div.autoPagerS a[title="Goto Window Top"]    { right: 63px !important; bottom: 0px !important; }
div.autoPagerS a[title="Page Up"]            { right: 42px !important; bottom: 0px !important; }
div.autoPagerS a[title="Page Down"]          { right: 21px !important;    top: 0px !important; }
div.autoPagerS a[title="Goto Window Bottom"] { right:  0px !important;    top: 0px !important; }

div.autoPagerS a[title="Goto Window Top"]    img { right: 0px !important; bottom: 0px !important; }
div.autoPagerS a[title="Page Up"]            img { right: 0px !important; bottom: 0px !important; }
div.autoPagerS a[title="Page Down"]          img { right: 0px !important;    top: 0px !important; }
div.autoPagerS a[title="Goto Window Bottom"] img { right: 0px !important;    top: 0px !important; }

div.autoPagerS span a:nth-of-type(2) {
    background: #c8f0ab !important;
    background: -moz-linear-gradient(top, #c8f0ab, #70d039) !important;
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#c8f0ab), to(#70d039)) !important;
    border: 1px solid #269f00 !important;
    -moz-border-radius: 9px !important;
    -webkit-border-radius: 9px !important;
    border-radius: 9px !important;
    color: #242 !important;
    font-size: 14px !important;
    font-weight: bold !important;
    height: 16px !important;
    line-height: 16px !important;
}

div.autoPagerS span a:nth-of-type(2):hover {
    background: #ff1933 !important;
    background: -moz-linear-gradient(top, #ff1933, #b20014) !important;
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#ff1933), to(#b20014)) !important;
    color: #fff !important;
    text-decoration: none !important;
}

div.autoPagerS a[title="Goto Window Top"],
div.autoPagerS a[title="Page Up"],
div.autoPagerS a[title="Page Down"],
div.autoPagerS a[title="Goto Window Bottom"] {
    background: transparent !important;
    border: none !important;
    height: 18px !important;
    width: 18px !important;
}

div.autoPagerS a[title="Goto Window Top"]:hover,
div.autoPagerS a[title="Page Up"]:hover,
div.autoPagerS a[title="Page Down"]:hover,
div.autoPagerS a[title="Goto Window Bottom"]:hover { z-index: 9999 !important; }

div.autoPagerS a[title="Goto Window Top"]:hover    { height: 28px !important; }
div.autoPagerS a[title="Page Up"]:hover            { height: 23px !important; }
div.autoPagerS a[title="Page Down"]:hover          { height: 23px !important; }
div.autoPagerS a[title="Goto Window Bottom"]:hover { height: 28px !important; }

div.autoPagerS a[title="Goto Window Top"]:hover    img { bottom: 10px !important; }
div.autoPagerS a[title="Page Up"]:hover            img { bottom:  5px !important; }
div.autoPagerS a[title="Page Down"]:hover          img { top:     5px !important; }
div.autoPagerS a[title="Goto Window Bottom"]:hover img { top:    10px !important; }

div.autoPagerS a[href="http://autopager.teesoft.info/help.html"] { display: none !important; }
div.autoPagerS a[name="xxAutoPagerimmedialate-load"]             { display: none !important; }
div.autoPagerS a[name="xxAutoPagerRate"]                         { display: none !important; }
div.autoPagerS a[name^="autopager_"]                             { display: none !important; }

div.AutoPager_Related { display: none !important; }

}
