/* AGENT_SHEET */
/*
 * YouTube - Halloween Design 
 *
 * Author      : HUMMER
 * Update      : October 8th, 2007
 * Website     : http://www.valacar.com/userstyles/
 * Description : Modifies the colors, logos, and a few other images on YouTube (http://youtube.com/)
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

body {
	background: #FF6600 !important;
	color: #ddd !important;
}

a:link, a:visited, a:active { color: #FFFF00 !important; }
:focus { outline: none; }
.eLink { border-bottom: 1px solid #FF0000 !important; }

/* Text colors */
span, div, p, li, td { color: #ddd !important; }
input *, textarea * { color: -moz-fieldtext !important; }


/* Text headers */
h1, h2, h3, h4, h5, h6, .hpBlockHeadingGray, .name, .title, #vidTitle, .wsHeading, .header, #catIndexGridHeading {
	color: #FF6600 !important;
}

/* Highlight Blocks */
#footContent, #channelVidsDiv, [style*="background-color: rgb(238, 238, 238)"], [style*="background-color: white"], #catIndexFeaturedWrapper, .memberContainer, .highlightBoxes, .highlightBoxesOpacity[class], .highlightBox, .attentionBox, #actionsAndStatsDiv, #statsBox, .settingDiv, #vResponseParent, #vrParentDiv, #localePickerBox, .notopbox, #relatedVidsBody, .changeSearchTypeLinks, .lowlightBox, .vNowPlaying, #guidelines, .highlightBoxBrowse, fieldset, .commentsTooltip, .cat-spotlight-box, #actionsAreaDiv .moreAction, #sendMessage td, #addToFavesResult, #copyrightText, #videoOwnerDiv {
	background: #282828 !important;
	border-color: #444 !important;
}

/* Various background color fixes */
[bgcolor="#000099"], [style*="background-color: rgb(255, 255, 255)"], #exContentWrapper, #exRelatedDiv, #exUserContentDiv, #exPlaylistDiv, #hpEmbedUnderBlock, .basicBoxes, .basicBoxesOpacity[class], .pagingDiv, #hpContentBlock, .vListBox, .previewBox, .vGridBox, .dBG, .rBG {
	background: #000 !important;
}
[bgcolor="#000000"], .eeeBG {
	background-color: #006600 !important;
}

/* Various border color fixes */
.contentBox, .commentPagination, .wsBody, #catIndexFeaturedVideo, #videoDetailsDiv, #videoStatsDiv, #embedDiv, #footTopDiv, #groupCommonHeader, .basicBoxes, .statsDiv, .vEntry, #sectionHeader, .bulletinTable td, .channelIcon, .channelIconWrapper, #catIndexGridHeading, .commentsTableFull td, .vListBox, .relatedDivider, #channelVidsBody, .myvSpacer, #viewPublicChannel, .vDetailEntry, .vGridBox, #preFooter, #searchSortContainer, .commHeading, .watchSectionBody, .searchFooterBox div {
	border-color: #66FF99 !important;
}

/* thumbnail borders */
.videoIconWrapperInner, .v90WrapperInner, .v75WrapperInner, .videobarthumbnail_white, .videobarthumbnail_gray { border-color: transparent !important; }
.videoIconWrapperOuter, .v90WrapperOuter, .v75WrapperOuter, [class^="vimg"] { border-color: transparent !important; }
.video-thumb-medium { border: none !important; }


/* Ads */
iframe, #bottomAdDiv, #leaderboardAd, .spOffersDiv, #sideContent, img[src^="/img/ad_"], a[href*="doubleclick.net"], #sideAd, img[src^="/img/pic_verizonwireless"], img[src*="pic_verizon_yellow_146x22.gif"], #FLASH_AD, #channelBrandCap
{
	display: none !important;
}

/* remove "Videos being watched right now" */
#hpMainContent #active_sharing_div
{
	display: none !important;
}


/* Purple and Yellow blocks
--------------------------*/
.hpPurpleBlock, .hpYellowBlock {
	background: #FF0000 !important;
	padding: 8px 10px !important;
	border: 1px solid #555 !important;
}

.hpPurpleBlock + img[src*="_300x"], .hpYellowBlock + img[src*="_300x"] { display: none !important; }


/* Channels masthead
---------------------*/
.channelMastheadTable {
	background: #FFCCFF !important;
	padding-bottom: 10px !important;
	-moz-border-radius-bottomleft: 9px !important;
	-moz-border-radius-bottomright: 9px !important;
}

img[src="/img/masthead_round.gif"] { display: none !important; }


/* Header and Comment Bars
---------------------------*/
.headerBox, .headerBoxOpacity {
	background-color: #333 !important;
	border-color: #222 !important;
}

.commentHead, #vResponseHeading, .commentHeadHidden { background: #333 !important; }
.commentHead { border-top: 1px solid #555 !important; }
.commentEntry { margin-bottom: .3em !important; }
.commentAuthHead { background: #33CCFF !important; }
.opacity80 { opacity: 0.7 !important; }
.commentSpam { background: #222 !important; }

.headerRCBox * { background: #333 !important; border-color: #333 !important; }


/* Featured Video tabs
-----------------------*/
.hilite {
	background-color: #000 !important;
	border-color: #000 !important;
}

#hpFeaturedAndSmallTabs {
	background-color: #333 !important;
}

#hpFeaturedHeading  { border-color: #FF0000 !important; }


/* Subscription box
-------------------*/
img[src*="pic_subsbox_"] { display: none !important; }
#hpSubsBoxTop, #hpSubsBox { background: #000 !important; border: none !important; }
#vbror, #vbrol { opacity: 0.3 !important; }
#hpSubsBox table td { background: #222 !important; }


/* Pager
--------*/
.pagerNotCurrent { background: transparent !important; color: #ccc !important; }
.pagerCurrent { background: #666 !important; color: #fff !important; border: 1px solid #999 !important; }

/* Main tabs
-------------*/
#gNavBottom {
	background: #FF6600 !important;
	-moz-border-radius-topleft: 10px !important;
	-moz-border-radius-topright: 10px !important;
}

#gNavDiv .tab_label { color: #aaa !important; }
#gNavDiv .tab_sel .tab_label { color: #fff !important; }

#gNavDiv .tab b {
	background: #222 !important;
	border-color: #444 !important;
	-moz-border-radius-topleft: 4px !important;
	-moz-border-radius-topright: 4px !important;
}

#gNavDiv .tab_sel b {
	background: #333 !important;
	border-color: #444 !important;
}

/* My Account */
#manageNav .selected, #manageContent [bgcolor="#cccccc"], #manageContent .controls {
	background: #333 !important;
}

#manageContent tr[bgcolor="#cccccc"] td { background-color: #333 !important; }

#manageContent {
	border-color: #CCFFFF !important;
}


/* Tweaks
----------------- */

/* expand thumbnail listings on videos page */
#mainContentWithNav {
	width: 705px !important;
}

/* horizontal menu at top right of page is an iframe sometimes */
iframe[src="/user_info"] {
	display: inline !important;
}

/* removed rounded corners on fieldsets */
.fieldsetTop, .fieldsetBottom {
	visibility: hidden !important;
}

/* QuickList current selection */
.playlistRowPlaying {
	background-color: #383838 !important;
	border-color: #555 !important;
}


