@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.acidcave.net") {
    .dead { /* nieobecni na chacie, ci, ktÃ³rzy ledwo wyszli i ci widoczni tylko pod magicznym okiem */
        padding-left: 13px;
        background: url('http://img28.imageshack.us/img28/9748/czaszkanieobecny.gif');
        background-repeat: no-repeat;
        background-position: left top;
        text-shadow: 5px 5px 10px #333333;
        color: black !important;
        
    }
    .butt {
       -moz-border-radius: 70px !important;
       border: none !important;
       color: #333366 !important;
       -moz-box-shadow: 0px 0px 5px #FFFFFF;
    }
    .butt:hover {
        -moz-box-shadow: 0px 0px 8px #FFCCCC; /* Czerwonawy pÅ‚omyk */
    }
    .userlist {
        border: 0px !important;
        -moz-border-radius: 10px;
        opacity: .95;
    }
    .userlist:hover {
        opacity: 1;
        -moz-box-shadow: 0px 0px 10px #333333;
    }
    
    input[name|="tresc"] {
        -moz-border-radius: 3px;
        border: 0px !important;
        height: 18px;
    }
    
    img[alt|="Czar 'Magiczne Oko' pozwala zobaczyÄ‡ ukryte za nagrobkami postaci!"] {
        opacity: 0.9;
        -moz-box-shadow: 0px 0px 5px #FF9999;
    }
    img[src|="undead/boneh5_left.png"] {
        opacity: 0.7;
    }
    img[src|="undead/boneh5.png"] {
       opacity: 0.7;
    }
    div[title|="Czat Kwasowej Groty"] {
        opacity: 0.9;
        -moz-box-shadow: 0px -20px 100px #BBBBBB;
    }
    div[title|="Czat Kwasowej Groty"] img:hover {
       opacity: 1;
    }
    div[title|="Czat Kwasowej Groty"]:hover {
       opacity: 0.95;
    }
    img:hover {
      opacity: 1; /* smoczki i inne rzeczy na wszelki wypadek */
    }
    .chattext {
        -moz-border-radius: 20px;
       border: 0px !important;
       margin-left: 2px;
        margin-top: 2px;
    }
    .chattext:hover {
      -moz-box-shadow: 0px 0px 10px black; /* mroczne podÅ›wietlenie */
    }
    /*[[kolorowanenicki]]*/
    .Ginden {
         font-weight: bold; /* mÃ³j prezent dla siebie.*/
         background: url('http://img254.imageshack.us/img254/2563/gindenek.gif');
         padding-left: 15px;
         background-repeat: no-repeat;
         background-position: left;
     
    }
    .Acid {
        font-weight: bold;
        color: red;
    }
    .Alamar {
        font-weight: bold;
        color: pink;
        padding-left: 15px;
        background: url('http://img841.imageshack.us/img841/6126/alamarek.gif'); /* SÅ‚it awatarek Alamara.*/
        background-repeat: repeat-y;
    }
    .Altair {
        font-weight: bold;
        color: cyan;
    }
    .Lucyfer {
        font-weight: bold;
        color: gray;
    }
    .Hubertus {
        font-weight: bold;
        color: gray;
    }
    span[class|="f!eld"] {
        font-weight: bold;
        color: gray;
    }    
    .Trang {
        font-weight: bold;
        color: gray;
    }
    .MCaleb {
        font-weight: bold;
        color: gray;
     }
    .Dragonar { /* Dragonar to szycha, niech ma*/
        font-weight: bold;
        color: purple;
        padding-left: 17px;
        background: url('http://img440.imageshack.us/img440/1979/dragus.png');
        background-repeat: no-repeat;
        background-position: left;
    }
    .Trith {
       
       padding-left: 10px;
       background: url('http://img696.imageshack.us/img696/9941/trithek.png');
       background-repeat: no-repeat;
    
    }
    .Hobbit {
    
        padding-left: 15px;
        background: url('http://img826.imageshack.us/img826/1678/hobbitek.png');
        background-repeat: repeat-y;
    }
    .Wiwern {
        padding-left: 17px;
        background: url('http://img101.imageshack.us/img101/6169/wiwernek.gif');
        background-repeat: no-repeat;
        background-position: left;
     }

