css:
@namespace url(http://www.w3.org/1999/xhtml);      
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 
 {

/*==========================================Body background==========================================*/

body {background: url('http://i52.tinypic.com/2qxp0jt.png') top #030303 repeat-x fixed !important;
color:#282e31 !important;font-size:12px !important;
}  
*{text-shadow:none !important;}
	
/* ====================Solid content area in front of background, behind content bubbles=======================*/

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color: rgba(10,10,10,0.5) !important;}
	
/*===============================Load More Posts Banner================================================*/

#auto_pagination_loader
	{background:rgbargba(10,10,10,0.03)!important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	opacity: .8 !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important; }	
	
	
/*=======================================Selection Nipple ================================================*/

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #4c66ab !important;}	
	
/*=======================================Right Column Highligts================================================ */
	
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	;-moz-box-shadow: 1px 1px 3px #a1bce3 !important;}

/*========================================================================================================
===============================================Dashboard==================================================
=========================================================================================================*/	
	
/*======================================Dashboard Icon Map=================================================*/
/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://img178.imageshack.us/img178/3291/2uyhzlcjpg.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}


	
/*==============================Right Column Tags (Dashboard and Tagged Pages)==============================*/	
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#bb6655 !important;  
	background-image:none !important;
	border:0px !important}


div.hide_overflow
	{font-size:.9em;}
a.tag,
a.explore_more_tags,
a.add_and_remove
	{height:8px;}
a.tag
	{position:relative;top:-4px;}
.selected  .tag
	{height:14px;background:#2d5b87 !important; 
	position:relative;top:0px;
	color:rgba(165,211,255, 0.60) !important;}
ul
	{-moz-box-shadow: 1px 1px 3px #a1bce3 !important;}
	
/*===========================================Right Column Radar Image============================================*/

div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#4f91e3 !important;
	opacity:.8; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}

/*========================================Blockquotes are crazy! ==============================================*/

blockquote
	{margin-left:-5px !important;}

/*===========================================================================================
=======================================Log In Page============================================
=============================================================================================*/

.login.sign_up,
A[href="/forgot_password"]{display: none !important;}	

/*===========================================================================================
===================================== Text Post Screen=========================================
=============================================================================================*/

.mceContentBody {
  background: none !important; }
  
/*==================================================
Notification Bubbles and other stuff I'm not messing with. :)
====================================================*/

ol#posts li.notification
	{background-color: #2c3e4d !important;
	border-bottom: 1px solid #5282c4 !important;
	color: #5282c4 !important;}
	
#right_column a#dashboard_switch_blog_menu_current:hover 
	{background:#2c3e4d;}
	
#nav .nav_item.active
	{background-color:#3e3e40;}
	
#right_column
	{color:#333333;}
	
#left_column #posts .post .permalink
	{background-color:#3e3e40;}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel
	{background-color:#3e3e40 !important;
	background-image:none !important;
	border:0px !important}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
	{background-image:none !important}

.notification_type_icon
	{display:none !important}

#left_column #posts .notification 
	{background-color:#3e3e40 !important;
	color:#666 !important;
	border-bottom-color: #eee !important}

#new_post_notice_container
	{color:#fff !important}

#new_post_notice_container a
	{color:#fff !important}
ol#posts .post.new_post .new_post_label 
	{display:none !important;}

	
}
