css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
refined reddit

10/04/2010 - initial release
01/30/2011 - added @-moz-document domain, @namespace, and meta information
01/31/2011 - more tweaks
02/01/2011 - more tweaks
02/15/2011 - links in link descriptions are colored blue
03/08/2011 - reset CSS on hovering warnings (e.g. "Please, no DAE posts. There is a subreddit for that.") and other tweaks
04/17/2011 - increase opacity for upvote/downvote arrows
*/

@-moz-document domain("www.reddit.com") {

* {
font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif !important;
}

body {
min-width: 1200px !important;
}

a {
color: #105CB6 !important;
}

#header {
height: 80px !important;
background: #cee3f8 !important;
border-bottom: 1px solid #afcce7 !important;
}

.tabmenu li a {
font-size: 13px !important;
border-color: #afcce7 !important;
padding: 4px 8px !important;
}

#sr-header-area {
border-bottom: 1px solid #999 !important;
}

#header-bottom-left {
height: 45px !important;
margin: 13px 20px 0 !important;
}

#header-bottom-left img {
height: 40px !important;
}

#header-bottom-right {
font-size: 12px !important;
font-weight: bold !important;
margin-top: -3px !important;
padding: 7px 15px 9px 15px !important;
background: #1B5790 !important;

top: 20px !important;
bottom: auto !important;

border-top-left-radius: 0 !important;
border-bottom-left-radius: 5px !important;
}

#header-bottom-right * {
color: #fff !important;
}

#header-bottom-right span.separator {
padding: 0 5px !important;
display: inline-block !important;
color: transparent !important
}

.content {
width: 70% !important;
margin: 20px !important;
background: transparent !important;
padding: 0 !important;
}

.side {
margin: 13px 20px 20px 0 !important;
width: 300px !important;
}

.side .content {
width: inherit !important;
min-width: inherit !important;
margin: inherit !important;
border-color: #CFDEF0 !important;
}

.side .content .thing {
padding: 10px !important;
}

form input[type=text], form input[type=password] {
border-color: #D8D8D8 !important;
}

#search input[type=text] {
padding: 5px 5px !important;
width: 288px !important;
}

#search input[type=submit] {
font-size: 18px !important;
margin-top: 5px !important;
}

#searchexpando {
margin: 5px 0 !important;
padding: 5px !important;
border-radius: 0 !important;
}

#searchexpando p {
margin: 0 !important;
}

form#login_login-main {
border-color: #D8D8D8 !important;
}

.raisedbox {
background: #F8F8F8 !important;
border-color: #CFDEF0 !important;
padding: 10px !important;
}

.menuarea {
border-bottom: 1px solid #CFDEF0 !important;
padding: 5px 0 !important;
margin: 0 !important;
}

#siteTable div.thing {
padding: 10px 0 10px 0 !important;
border-bottom: 1px solid #CFDEF0 !important;
margin: 0 !important;
}

#siteTable_organic, #siteTable div.thing.promotedlink {
padding: 0 !important;
background: #F8F8F8 !important;
border-color: #CFDEF0 !important;
}

#siteTable_organic div.thing, #siteTable div.thing.promotedlink {
padding: 10px 0 10px 0 !important;
}

#siteTable_organic .rank {
width: 0 !important;
}

#siteTable_organic .link p.title {
margin-right: 66px !important; // 52px + 4px + 10px (padding)
}

#siteTable_organic .sponsored-tagline {
display: none !important;
}

#siteTable_organic .nextprev {
top: 5px !important;
}

#siteTable_organic .link .midcol, #siteTable .link .midcol {
padding: 7px 4px !important;
margin: 0 15px 0 0 !important;
width: 40px !important;
height: auto !important;
}

#siteTable_organic .link .midcol, #siteTable div.thing.promotedlink .midcol {
padding: 4px !important;
}

#siteTable .link .midcol {
background: -webkit-gradient(radial, 20 25, 3, 20 25, 30, from(#FFFAE7), to(#FFF095)) !important;
margin: 0 15px 0 0 !important;
color: #A6A45E !important;
padding: 7px 4px !important;
border-radius: 3px !important;
}


#siteTable div.thing.promotedlink .midcol {
background: transparent !important;
}

.midcol .arrow {
opacity: 0.9;
}

.midcol * {
font-size: 13px !important;
}

#siteTable .midcol .score {
padding: 2px 0 !important;
font-weight: normal !important;
font-size: 15px !important;
text-shadow: 0 -1px 0 #fff;
}

.side .link .midcol {
margin: 0 15px 0 0 !important;
}

#siteTable .rank {
display: none;
}

.keyHighlight {
border: 0 !important;
background: transparent !important;
}

.keyHighlight a, .keyHighlight p.title a {
color: #EA633F !important;
}

.last-clicked {
background: transparent !important;
border: 0 !important;
}

.link.odd {
background: transparent !important;
}

.link.odd .entry {
border: 0 !important;
}

.title {
font-weight: bold !important;
}

p.title {

}

p.title * {
font-size: 18px !important;
font-weight: bold !important;
}

p.title a {
color: #105CB6 !important;
text-decoration: underline !important;
}

.thing p.title .domain, .thing p.title .domain a {
color: #d36200 !important;
text-decoration: none !important;
}

.link * {
color: #666 !important;
font-size: 13px !important;
}

.link a {
color: #105CB6 !important;
}

.link a:visited {
color: #86B0E1 !important;
}

.thing .title.click, .thing a.title:visited {
color: #86B0E1 !important;
}

.expando-button {
margin-top: 3px !important;
}

.thumbnail {
border: 1px solid #CFDEF0 !important;
padding: 3px 3px 0 3px !important;
}

.entry ul.flat-list.buttons li a {
color: #578CCA !important;
font-weight: normal !important;
}

.tagline, .flat-list.buttons {
font-size: 12px !important;
line-height: 18px !important;
}

.link .usertext .md {
border-radius: 0 !important;
background: transparent !important;
border: 0 !important;
border-left: 1px solid #F0F0F0 !important;
padding-left: 15px !important;
}

p.nextprev {
text-indent: -1000px;
overflow: hidden;
background: #eee;
border-radius: 6px;
padding: 7px !important;
margin-top: -6px !important;
height: auto !important;
line-height: 0 !important;
}

p.nextprev a {
background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#fafafa), to(#f0f0f0)) !important;
border: 1px solid #999;
border-radius: 6px;
color: #333 !important;
width: 50px;
height: 30px;
text-align: center;
display: block;
text-indent: 0;
text-transform: capitalize;
font-weight: bold !important;
font-size: 12px !important;
line-height: 30px !important;
}

p.nextprev a[rel*="prev"] {
float: left;
}

p.nextprev a[rel*="next"] {
float: right;
}

p.nextprev a:hover {
border-color: #666;
}

p.nextprev a:active {
background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#f0f0f0), to(#fafafa)) !important;
border-color: #666;
}

#progressIndicator {
font-weight: bold !important;
background: #F8F8F8 !important;
border-color: #D8D8D8 !important;
border-radius: 5px !important;
color: #999;
height: 30px !important;
line-height: 30px !important;
margin-top: 20px !important;
}

#progressIndicator img {
display: none !important;
}

.footer * {
line-height: 18px;
}

.footer .col {
border-color: #F0F0F0 !important;
}

.subreddit .usertext .md {
border-radius: 0 !important;
}

.titlebox .usertext-body .md ol {
position: relative !important;
z-index: 1 !important;
top: 0 !important;
}

.titlebox .usertext-body .md ol {
left: 0 !important;
display: inherit !important;
}

.titlebox .usertext-body .md ol li {
margin: 0 !important;
margin: 5px 0 !important;
}

.titlebox .usertext-body .md ol li a {
border-radius: 0 !important;
margin: 0 !important;
display: inline !important;
border: 0 !important;
background: 0 !important;
padding: 0 !important;
font-style: inherit !important;
font-weight: bold !important;
font-size: 13px !important;
}

.toggle a {
color: #fff !important;
}

}
