@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(google.pl) {
	body, label, nobr, table, td, h1, h2, h3 {
		color: #1745a7 !important;
	}
	a {
		color: #1745a7 !important;
		text-decoration: none !important;
		border-bottom: 1px solid #f6c707 !important;
		
	}
	a:hover {
		border-bottom: 1px solid #f69f07 !important;
	}

	input {
		color: #1745a7 ;
		background-color: #fbfbfb;
		border: 1px solid #f69f07;
		border-bottom-color: #f6c707;
		border-right-color: #f6c707;
		margin: 5px;
	}
	input[type="submit"] {
		color: #f60707;
	}
	input:hover {
		background-color: #fdf5e5;
	}
	/** Usuwa border dla linku z logo */
	a[href^="/search?q="] {
		border: 0px !important;
	}
	a[href^="/search?q="]:hover {
		border: 0px !important;
	}
