css:
/*#################################################* 

   PROJECT:      GameFAQs - SlimFit (+No Ads)        
   AUTHOR:       BrutalAl                            
   VERSION:      2010.10.09.0500                     
   STYLES PAGE:  http://userstyles.org/users/64751   

 *#################################################*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.gamefaqs.com/") {

  /*--START Logo Row--*/

    /*row height*/
    #header, #header .mh_wrap {
      height:67px !important;
      background-position:0 -60px !important;
    }

    /*gamefaqs logo*/
    #logo {
      background-position:0 -7px !important;
      height:66px !important;
    }

    /*login box - signed in*/
    #loginbox {
      background-position:0 3px !important;
      height:56px !important;
    }

    /*login box - not signed in*/
    #loginbox .msg {
      padding-top:8px !important;
    }

    #loginbox form {
      padding-top:0 !important;
    }
  /*--END Logo Row--*/


  /*--START Search Row--*/

    /*search row background*/
    #searchbox, #searchbox .mh_wrap {
      background-position:0 -207px !important;
    }

    /*search row height*/
    #searchbox .mh_wrap {
      height:24px !important;
      padding-top:1px !important;
    }

    /*search row search element height*/
    #searchbox form {
      height:24px !important;
    }

    /*searchbox framing height and padding*/
    #searchbox li.search_term .data {
      height:13px !important;
      padding-bottom:2px !important;
    }

    /*searchbox inner height*/
    #searchbox li.search_term input {
      height:14px !important;
    }

    /*go-button height*/
    span.go {
      height:18px !important;
    }

    /*go-button text position*/
    .submit .go span {
      line-height:18px !important;
    }

    /*sponsored links - white text*/
    .xpromolink, .rubics_netxp1_main {
      color:#B4B4B4 !important;
    }

    /*sponsored links - red text*/
    .xpromositename {
      color:#A06868 !important;
    }
  /*--END Search Row--*/


  /*--START Quick Nav Row--*/

    /*quicknav row top border white line*/
    #masthead #quicknav {
      border-top-color:#FFFFFF !important;
      border-top-style:solid !important;
      border-top-width:1px !important;
    }

    /*quicknav row height background positioning*/
    #quicknav, #quicknav .mh_wrap {
      height:25px !important;
      background-position:0 -259px !important;
    }

    /*quicknav content height and positioning*/
    #quicknav a {
      height:25px !important;
      line-height:26px !important;
    }

    #quicknav p {
      margin-top:-1px !important;
    }
  /*--END Quick Nav Row--*/


  /*--START Platforms Row--*/

    /*platforms row height*/
    #platformlist .mh_wrap {
      height:25px !important;
    }

    /*links positioning*/
    #platformlist .nav {
      padding-top:6px !important;
    }

    /*jumper dropdown positioning*/
    #platformlist div.jumper {
      padding-top:4px !important;
    }

    /*ads row below platforms row*/
    .ad_wrap {
      display:none !important;
    }
  /*--END Platforms Row--*/


  /*--START Main Content--*/

    /*main content start, top rows end*/
    #masthead {
      height:144px !important;
    }

    /*main content top positioning*/
    #gutters {
      margin-top:10px !important;
    }

    #gutter_top {
      padding-top:0px !important;
    }

  /*--START Game Pages--*/

    /*crumbs-path positioning*/
    div.content_nav {
      padding-right:0px !important;
    }

    div.content_nav .crumbs {
      float:right;
    }

    /*game title*/
    div.content_nav h1 {
      margin-bottom:6px !important;
      margin-top:6px !important;
      margin-left:-5px !important;
    }

    /*game navbar size and positioning*/
    div.content_nav {
      border-bottom-style:solid;
      border-bottom-width:1px;
      border-bottom-color:#A0B4FF;
    }

    #content ul.content_nav {
      margin-bottom:-1px !important;
    }

    ul.content_nav li a b {
      height:30px !important;
      line-height:32px !important;
    }

    ul.lower {
      margin-top:0px !important;
    }
}

    /*no right column, "/boards" pages only*/
    @-moz-document url-prefix("http://www.gamefaqs.com/boards/") {

      #mpu_col_wrap {
        background-image:none !important;
        margin-top:0px !important;
        padding-bottom:0px !important;
      }

      #mpu_col_wrap .col_layout {
        padding-right:10px !important;
      }

      #mpu_col_wrap #side_col {
        display:none !important;
      }
    }

@-moz-document url-prefix("http://www.gamefaqs.com/") {

    /*board head, user panel head*/
    #content .pod .user_panel .body {
      margin-top:0px !important;
      padding-left:10px !important;
      border-bottom-color:#CCCCCC;
      border-bottom-style:solid;
      border-bottom-width:1px;
      height:23px !important;
    }

    #content .pod .user_panel .body span {
      padding-top:5px !important;
    }

    /*board head, user panel body*/
    .u_search {
      height:26px !important;
      padding-bottom:0px !important;
      padding-left:0px !important;
      padding-right:0px !important;
      padding-top:6px !important;
    }

    /*board head, user panel body actions*/
    #content .links {
      float:left !important;
      padding-bottom:0px !important;
      padding-left:6px !important;
      padding-right:0px !important;
      padding-top:6px !important;
    }    

    #content .pod .user_panel .u_search .links li {
      display:inline !important;
      padding-top:0px !important;
      padding-bottom:0px !important;
      padding-left:3px !important;
      padding-right:5px !important;
      border-right-style:solid;
      border-right-width:1px;
      border-right-color:#808080;
    }

    /*board head, user panel body search*/
    .u_search form {
      float:left !important;
      height:26px !important;
      padding-bottom:0px !important;
      padding-left:0px !important;
      padding-right:0px !important;
      padding-top:0px !important;
      background-color:#CCCCCC;
    }

    /*board head, forum search input framing*/
    #content .pod .user_panel .u_search .data {
      background-color:#EEEEEE;
      margin-left:-1px !important;
      padding-left:4px !important;
    }

    /*board head, forum navigation*/
    .u_pagenav {
      padding-bottom:0px !important;
      padding-left:0px !important;
      padding-right:0px !important;
      padding-top:0px !important;
      text-align:right !important;
      position:relative !important;
      right:10px !important;
      bottom:20px !important;
      float:right !important;
      border-style:hidden !important;
      height:0px;
    }

    /*topics list, top margin*/
    #full_col_wrap {
      margin-top:5px !important;
    }

    /*topics list, split list note*/
    div#board_wrap.pod p {
      margin-bottom:5px !important;
      margin-top:0px !important;
    }

    /*topics list, bottom page jumper*/
    #content .pagejumper {
      margin-bottom:0px !important;
      margin-left:5px !important;
      margin-top:5px !important;
      width:auto !important;
    }
  /*--END Game Pages--*/


    /*main content, google ads*/
    #content #sponsored_links {
      display:none !important;
    }

    /*main content, various non-forum adjustments*/
    #sky_col_wrap .col_layout {
      padding-right:10px !important;
    }

    #content #sky_col_wrap .col_layout .pod .body,
    #content #mpu_col_wrap .col_layout #main_col #board_wrap.pod .body,
    #content #gamespace_noback .pod .body {
      padding-left:0px !important;
    }

    #gamespace_noback #mpu_col_wrap {
      margin-top:0px !important;
    }
  /*--END Main Content--*/

}
