css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yahoo-mbga.jp") {
 * {
  background-image:none !important;
  text-indent:0px !important;
  word-spacing:normal !important;
  letter-spacing:normal !important;
  font-size:13px !important; /*←基準の文字サイズ*/
 }
 body {
  color:#333 !important;
  background-color:#fff !important;
 }
 a {
  color:#00f !important;
  background-color:#fff !important;
  text-decoration:underline !important;
  font-size:1em !important;
  line-height:1.2em !important;
  white-space:nowrap !important;
  width:auto !important;
  height:auto !important;
 }
 input,textarea {
  color:#000 !important;
  font-size:1em !important;
  border:1px solid #666 !important;
  background-color:#999 !important;
 }
 div#global-lay li {
  width:auto !important;
 }
 div#global-lay .lv6-lay {
  display:block !important;
  margin-top:1.2em !important;
 }
 div#global-lay .lv6-lay a {
  display:inline !important;
  padding-left:10px !important;
 }
 .new-flg {
  color:#c66 !important;
  font-size:10px !important;
  white-space:nowrap !important;
 }
 .sort-lay, .sort-lay li, .delete-btn {
  width:auto !important;
 }
 img,object {
  display:none !important;
 }
 #gamenav, #gamemain, #slideshow, .bnr1 {
  display:none !important;
 }
 div.login-main-lay, div.blk2-lay, h2.png_bg {
  display:none !important;
 }
 form#login-form a {
  display:block !important;
  width:auto !important;
  height:auto !important;
  float: none;
  clear: both;
 }
 td.td1-lay img {
  display:block !important;
 }
}

@-moz-document domain("login.yahoo.co.jp") {
 div#yregtxt {
  display:none !important;
 }
}
