css:
@namespace url(http://www.w3.org/1999/xhtml);      @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 
 {


#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members, #dashboard_inbox, #main_tag, #dashboard_likes, #dashboard_following, #dashboard_tumblelog_settings, #dashboard_directory, #dashboard_settings {background: url('http://img178.imageshack.us/img178/4417/slytherinbg.png') top left #004a28 repeat fixed !important; font-size:10px;}  
*  {text-shadow:none !important;}
	

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:rgba(0, 0, 0, 0.95)!important;}

	

#auto_pagination_loader
	{background:rgba(187,103,85, 0.00)!important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	opacity: .5 !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important; }	
	
	

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #698878 !important;}	

	
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: rgba(255,255,255, 0.50) !important; 
	background: #283832 !important;}



	
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#2a3d33 !important;  
	background-image:none !important;
	border:0px !important}

div.hide_overflow
	{font-size:.9em;}
a.tag,
a.explore_more_tags,
a.add_and_remove
	{height:8px;}
a.tag
	{position:relative;top:-4px;}
.selected  .tag
	{height:14px;background:#283832 !important; 
	position:relative;top:0px;
	color:rgba(255,255,255, 0.50) !important;}
ul
	{-moz-box-shadow: 1px 1px 3px #283832 !important;}
	

div.gradient,
div.dashboard_nav_item, 

	{background-color:transparent !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}


blockquote
	{margin-left:-5px !important;}



.login.sign_up,
A[href="/forgot_password"]{display: none !important;}	


.mceContentBody {
  background: none !important; }
  


	
#right_column a#dashboard_switch_blog_menu_current:hover 
	{background:#e5dfcf;}
	
#nav .nav_item.active
	{background-color:#d4cdb9;}
	
#right_column
	{color:#333333;}
	
#left_column #posts .post 
	{background-color:#d4cdb9;}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel
	{background-color:#d4cdb9 !important;
	background-image:none !important;
	border:0px !important}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
	{background-image:none !important}

#left_column #posts .notification {
  background-color:#2a3d33 !important;
  color:#49705c !important;
  border-bottom-color: #49705c !important
}
#left_column #posts .notification .block {
  color:#49705c !important;
}
#left_column #posts .notification.alt {
  background-color: #334e40 !important;
}
#left_column #posts .notification.last_notification,
#left_column #posts .notification.single_notification {
  border-bottom-color:#49705c !important;
}
#left_column #posts .notification a {
  color:#49705c !important;
  border-bottom:0px !important;
}
#left_column #posts .notification blockquote {
  border-left-color:#49705c !important;
}
#left_column #posts .notification .avatar {
  border-color: !important;
  background-color:#334e40 !important;
  opacity:1 !important;
}
#left_column #posts .notification .avatar:hover {
  opacity:1 !important;
}


.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(26,49,110, .85) !important;
  border-top-right-radius: 23px !important;
  border-bottom-left-radius: 10px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}
.permalink:hover {
background: rgba(169,130,127, 1.0) !important;
}
.footer_links with_tags with_source_url, .source_url, .tags{font-size:10px; color:#d0d0d0; font-family:verdana;-webkit-transition: all 0.6s ease-out;-moz-transition: all 0.6s ease-out;transition: all 0.6s ease-out;}
.post_info{font-size:8px; color:#d0d0d0; font-family:arial;}



ol#posts li.post.new_post {
background: #ffffff url('http://img201.imageshack.us/img201/3291/2uyhzlcjpg.png') center center no-repeat;
}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
opacity: .00 !important;
}
.new_post_label{
font-size:10px !important;
padding-top:15px !important;
font-family: sans-serif !important;
font-style:normal !important;
text-transform: uppercase !important;
color:#000 !important;
letter-spacing:1px !important
}


}
