css:
/*heise.de - dark -- 2011-09-22*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(heise.de) {
#bannerzone, div.cadv, div.bcadv, div.contentbanner, div.sales, div.online-markt, div.adbottom, #navi_top .zweite_zeile, div.naviad,div.teaser_adliste,.contentbanner, div.suchen_de, div.teaser_geizhals
{
display: none !important;
}
#container_content, #sitemap
{
top: 0 !important;
}
body,#mitte #mitte_links, #mitte, #mitte #mitte_rechts .col100weiss, .fliess, #navi_top .erste_zeile, #topteaser, #anrissliste, #sub_navi,#bg_bottom,#fussbereich .vertical_line,#links, #links .farbe a, #mitte_links .oben_navi a, #meta_navi,#mitte_rechts .teaser_tabs h4,#mitte_rechts .teaser_tabs .ui-tabs-nav .ui-state-active a,#mitte_rechts .teaser_preisvergleich h4,#mitte_rechts .teaser_preisvergleich form,div.teaser_ebook,#navi_bottom .international,.lefttable,#mitte_dreispalten #linie_links,.blogfliess,#rechts_teaser .teaser_fiver h4,#rechts_teaser .teaser_fiver .nummer a:hover, #rechts_teaser .teaser_fiver .nummer .activeSlide,#suchfeld input.textfield, #suchfeld_foren input.textfield,.suchen,#mitte_rechts .teaser_fiver .nummer a:hover, #mitte_rechts .teaser_fiver .nummer .activeSlide,#navi_login,#navigation li.aktiv,#searchbox input.textfield,#mitte_rechts .kaesten h4,#login_breadcrumb, div.teaser_fiver,.navigation #navi li .subnavi
{
background: black !important;
color: grey !important;
}
#mitte_rechts h4 a, #mitte .ueberschrift a, #mitte_artikel h1,.rubrik,.zitat,.date-cell,.kasten-p, #mitte_rechts p a span
{
color: white !important;
}
#mitte #mitte_rechts,#mitte_news .weitere, #sitemap, #navi_top, #navi_bottom, #navigation, #breadcrumb,#tabbox #tabs-1, #tabbox #tabs-2,.leftcontent, .rightcontent,#mitte_links .fussbereich,#mitte_rechts .teaser_tabs .ui-tabs-nav a,#mitte_rechts .teaser_preisvergleich,.date-cell,#mitte_dreispalten #mitte_links h1#newsheader,#extra_spalte_rechts .linkliste .linkliste_title, #extra_spalte_rechts .kaesten h4, #extra_spalte_rechts .softwarekasten h4,h4.erg_filter,#suche_reiter li a.aktiv, p.news_option,
#mitte_rechts .ui-tabs-nav .ui-state-active a, #mitte #teaser_section .ui-tabs-nav .ui-state-active a,.leseprobe, .hintergrund, .artikel_datum,.navigation #navi > li.aktiv,.navigation #navi li .subnavi li:hover, .navigation #navi li .subnavi li:hover a, .navigation #navi li div.subnavi a:hover,#mitte_weiss .hproduct #details_mitte dl dt:nth-of-type(odd), #mitte_weiss .hproduct #details_mitte dl dd:nth-of-type(odd)
{
background: hsla(0, 0%, 100%, 0.1) !important;
}
a:link
{
color: #ADD8E6 !important;
}
a:visited, #breadcrumb,.righttable,.buch-fliess,
#mitte_start .news_topteaser p a, #mitte_links .anriss_mit_bild_links p a, #mitte_links .teaser_hotline dd a, #mitte_links .teaser_artikel p a, #mitte_rechts .teaser_artikel p a, #mitte_rechts .teaser_frei a .textzeile, #mitte_rechts .teaser_fiver a .textzeile, #rechts_teaser .teaser_mit_bild_und_text a .textzeile, #rechts_teaser .teaser_tabs .ui-tabs-panel a .info
{
color: lightgrey !important;
}
#navi_top a, #navi_bottom a, #container_content, .related_items h4,#mitte_dreispalten,#links .farbe ul,#mitte_links .news_topteaser,#navi_top_container,.righttable,.blogtable,.bloghead,#rechts_teaser .teaser_fiver .nummer a,#softlink,#mitte_dreispalten .anrissliste, #magazin .anrissliste,.date-table,.position_1,.topteaser,#alertliste,#extra_spalte_rechts .linkliste, #extra_spalte_rechts .kaesten, #extra_spalte_rechts .softwarekasten,#links .farbe,#mitte_rechts .teaser_fiver .nummer a,#galerie li,#galerie,#mitte_rechts .abgesetzt,#subnavi ul,#suche_reiter li a,.webcode,#mitte_rechts .kasten_box h4, #mitte_rechts .kasten_solobox h4,#navi_top ul li,
#mitte_rechts .ui-tabs-nav a, #mitte #teaser_section .ui-tabs-nav a,.navi_top_container, #kopf,.teaser,#logo_bereich,#logo_bereich .navigation,#logo_bereich .navi_suche, .fiver,#mitte_rechts .teaser_laenderliste, #mitte_rechts .teaser_handygalerie,#mitte_weiss,#mitte
{
background: transparent !important;
}
#container
{
width: auto !important;
}
a.link:hover,#navigation .zweite_ebene, .pos-spalte, .spalte, .up-spalte, .main, .leftbox, .kasten, .kasten-top,.f-kasten
{
color: lightgrey !important;
background:transparent !important;
}
#links li,.date-table,.inhalt-table-2,#extra_spalte_rechts .linkliste .linkliste_title, #extra_spalte_rechts .kaesten h4, #extra_spalte_rechts .softwarekasten h4,#extra_spalte_rechts .linkliste li, #extra_spalte_rechts .kaesten li, #extra_spalte_rechts .softwarekasten li,#mitte_links .news_topteaser,#mitte_links .news_topteaser #meldungen,#mitte_news .trennlinie_6px
{
border-color: hsla(0, 0%, 100%, 0.1) !important;
}
#alertliste li, a.archiv-x
{
background-color: transparent !important;
}
.related_items ul li,#mitte_uebersicht .anriss_rubrik li, #mitte_uebersicht .anriss_blog li, #mitte_uebersicht .anriss li
{
background: transparent !important;
list-style: disc !important;
padding-left: 0 !important;
margin-left: 20px !important;
color:grey!important;
}
.nonpublic
{
opacity:0.4!important;
}
.mainframe
{
position:inherit!important;
}
}
