css:
/*gentoo.org - dark -- 2010-10-09*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gentoo.org") {
body,.content,td[bgcolor="#ffffff"],.news {
background-color: black !important;
color: grey !important;
}
p.secthead,.tableinfo {
background-color: #232323 !important;
color: grey !important;
}
span.code {
color :white !important;
}
pre {
background-color: #121212 !important;
color: white !important;
}
span.code-input {
color: #40c100 !important;
}
p.codetitle {
background-color: #343434 !important;
color: white !important;
}
p.note,.alttext
{
color: white !important;
}
td[bgcolor="#bbffbb"] {
background-color: #234023 !important;
}
td[bgcolor="#ffbbbb"] {
background-color: #402323 !important;
}
td[bgcolor="#ffffbb"] {
background-color: #404023 !important;
}
td[bgcolor="#45347b"] {
background-color: #343434 !important;
height: 64px !important;
}
td[bgcolor="#45347b"] > a > img {
background-color: #343434 !important;
height: 64px !important;
}
td[bgcolor="#dddaec"],.altmenu {
background-color: #121212 !important;
color: grey !important;
}
a:link {
color: #ff8000 !important;
}
}
