@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vukajlija.com") {

body{
background: #353535 !important;
}
.post h2, .post h2 a {
font-size: 18px !important;
font-family: segoe ui !important;
font-weight:bold !important;
color: #AFAFAF !important;
}
.post .copy p {
font-family: segoe ui !important;
}
.post-container .meta li a {
font-family: segoe ui !important;
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
font-family: segoe ui !important;
}
a.parent-link:hover{
background: #353535 !important;
}
a.selected-page{
background: #353535 !important;
border: 1px solid #AFAFAF !important;
}
a.selected-page:hover{
background: #353535 !important;
}
ul.page_button_content li a:hover, ul.page_button_content li span:hover {
background: #AFAFAF !important;
}
input.submit_button, a.action-link {
color: #000 !important;
background: #2f2f2f !important;
height:25px!important;
border: 2px outset #AFAFAF !important;
cursor:pointer;
padding:2px!important;
}
input.submit_button:hover{
color: #000 !important;
background: #afafaf !important;
border: 2px inset #000 !important;

}
#page-nav-container .thing, #page-nav-container ul {
padding: 0!important;
}
.post-container,.comment-container,.comment-form-content,.large-advert,.widget,#post-content,.page-nav,.pagination-container{
-moz-box-shadow: 1px 2px 5px #000!important;
-webkit-box-shadow: 1px 2px 5px #000!important;
-o-box-shadow: 1px 2px 5px #000!important;
}
}
