css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") 
{
	#jewelCase .jewelCount *
	{background-color: red !important;}

	#fbPhotoTheater .stageWrapper,
	#fbPhotoTheater .stageWrapper *,
	#fbPhotoTheater .positioner,
	#fbPhotoTheater .positioner *,
	#fbPhotoTheater #fbPhotoTheaterStageActions,
	#fbPhotoTheater .stageBackdrop,
	.fbJewel, .jewelButton, #jewelContainer,
	.fbJewelFlyout *	{
	border-color: transparent !important;
	outline-color: transparent !important;
	background-color: transparent !important}
	
	.uiMenu, .fbJewelFlyout
	{border-color: #3B5998 #3B5998 #3B5998 !important}
	.fbJewelFlyout .jewelHighlight li a:hover, 
	.fbJewelFlyout .jewelHighlight li a:active, 
	.fbJewelFlyout .jewelHighlight li a:focus
	{background-color: #3B5998 !important;
	border-top: 1px solid #3B5998 !important;
	border-bottom: 1px solid #3B5998 !important}
	
	.uiUfi .ufiItem
	{background-color: #222 !important}
	
	.uiUfi .ufiItem *
	{background-color: transparent !important}
	*, #fbPhotoTheater .tagName,
#jewelContainer .openToggler .jewelButton	{
		background-color: black !important;
		color: white !important;
		border-color: #112 !important;
	}

	.uiTypeahead,
	.uiTypeahead .wrap,
	.webComposerFluidInputTable .fluidInputCell,
	textarea, .inputtext, .inputpassword
        {border-color: grey !important;}        

	.pollOptions .selectedPollOption div, 
	.pollOptions .pollResultsBar:hover div, 
	.pollOptions .pollRadioBtn:hover + .pollResultsBar div
	{border-color: #3B5998 !important}
	
	#mainContainer a,
	.fbQuestionsPollResultsBar .label .fwb,
	.fcg
	{color: #C0CADE !important}

	#jewelCase,
	#jewelCase *,
	#pageLogo *, 
	#pageHead,
	.fbNubFlyoutTitlebar, 
	.fbNubFlyoutTitlebar *
	{background-color: #3b5998 !important;
	border-color: transparent !important}

	#headNavOut *, #headNavOut, 
	#pageNav *, #headNav *, #headNav,
	.uiSideNav .selectedItem .item, 
	.uiSideNav .selectedItem .item:hover, 
	.uiSideNav ul .selectedItem .subitem, 
	.uiSideNav ul .selectedItem .subitem:hover
	{
	background-color: #627aad !important;
	border-color: #627aad  !important;
	outline-color: #627aad  !important;} 

	.fbQuestionsPollResultsBar .shaded
	{background-color: #627aad !important;
	outline-color: #93A4C7 !important;
	border-style: solid !important}

	#jewelCase a:hover, 
	#jewelCase a:focus, 
	#jewelCase a:active,
	#jewelCase .jewelBox 
	{background-color: #627AAD !important}
	
	
	.uiSelector .openToggler .uiSelectorButton, .uiSelector .openToggler .uiSelectorButton:active, .uiSelector .openToggler .uiSelectorButton:focus, .uiSelector .openToggler .uiSelectorButton:hover,
	.uiMenuItem a:active, .uiMenuItem a:focus
	{background-color:#3B5998 !important;
	border-color:#3B5998 !important}
	
	
	
	#headNav a:hover, 
	#headNav a:focus, 
	#headNav a:active,
	#navSearch a:hover, 
	#navSearch a:focus, 
	#navSearch a:active,
	#jewelCase .jewelBox a:hover, 
	#jewelCase .jewelBox a:focus, 
	#jewelCase .jewelBox a:active
	{
	background-color: #6D86B7 !important;
	outline: medium none !important;}

	#navSearch *,
    .uiTooltip .uiTooltipWrap .uiTooltipText,
.photoInfoWrapper
	{background-color: black !important;
	border-color: black !important}

	.profile_tab_more .tab_link:hover, 
	.newsfeed_plus_link a:hover,
	.add_tab .tab_link:hover
	{background-color: #111111 !important;
	padding:25px 50px 75px 100px !important}

	.uiButton .uiButtonText, 
	.uiButton input {
		background-color: transparent !important;
		color: black !important;
	}

	#mainContainer > #leftCol, 
	#mainContainer > #leftCol *,
	#mainContainer > #contentCol > #rightCol,
	#mainContainer > #contentCol > #rightCol *,
	.pollResultsBar,
	.fbQuestionsPollResultsBar,
	.fbQuestionsPollResultsBar .label,
	.fbQuestionsPollAttachmentContainer,
	.fbQuestionsPollAttachment,
	.fbQuestionsPollClickTarget,
	.fbQuestionsPollClickTargetWithAttachment,
	.uiButton .img,
	.uiBubbleCount span.numberContainer .countValue,
	.uiSideNav .selectedItem .item *,
	.uiSideNav ul .selectedItem .subitem *,
	#jewelCase .jewelBox *,
	.uibutton,
	.itemLabel 
	{background-color: transparent !important;}
	 
	.uiButtonSpecial .uiButtonText, 
	.uiButtonSpecial input, 
	.uiButtonSpecial.uiButtonDisabled .uiButtonText, 
	.uiButtonSpecial.uiButtonDisabled input, 
	.uiButtonConfirm .uiButtonText, 
	.uiButtonConfirm input,
	.uiButtonConfirm.uiButtonDisabled .uiButtonText, 
	.uiButtonConfirm.uiButtonDisabled input,
	.fbx_stream_header div.filterListSelector .uiSelectorButton .uiButtonText
	/*,
	a[rel="toggle"] span[class="uiButtonText"] */
	{Color: white !important;}
	
	.middle .content * , .middle .content,
	.uiStepList, .ol
	{color: black !important;}

}
