@-moz-document domain(tumblr.com) {
#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_followers, #dashboard_members {background: url('http://media.tumblr.com/tumblr_l9ist6jB2t1qazf3y.png') center right #000000 repeat fixed !important;}
#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: #2E2E2E !important;}#dashboard_edit_post{background-color: #ffffff !important}#right_column .dashboard_nav_item {border-bottom:0px solid #eee !important;background-image:none  !important}#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a,#right_column .dashboard_nav_item a,#right_column .dashboard_nav_item ul.dashboard_subpages,#right_column .dashboard_nav_item ul.dashboard_subpages li a {color:#fff !important;}#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {color:#888 !important ;}#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {background: none !important;}#left_column #posts .notification .avatar {border-color:transparent !important;background-color:#fbfbfb !important;}#left_column #posts .notification .dashboard_switch_blog_menu_header{background-color:#fbfbfb !important;color:#666 !important;}.dashboard_switch_blog_menu_item{
background-color:#fbfbfb !important;color:#666 !important;border:0px !important}.dashboard_switch_blog_menu_footer{background-color:#fbfbfb !important;color:#666 !important;}.dashboard_switch_blog_menu_wrapper{background-color:#fbfbfb !important;}#new_post_notice_container{color:#fff !important}#new_post_notice_container a{
color:#fff !important}#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_and_divider,#dashboard_switch_blog_arrow_current{background-image:none !important}/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i55.tinypic.com/260uzwo.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}.new_post_label{font-size:14px !important;padding-top:10px !important;font-family:Georgia, Times New Roman, sans-serif !important;font-style:italic !important;text-transform: lowercase !important;color:#222 !important;letter-spacing:1px !important}#left_column #posts .notification {background-color:#fbfbfb !important;color:#666 !important;border-bottom-color: #eee !important}#left_column #posts .notification .block {color:#333 !important;}#left_column #posts .notification.alt {background-color: #f7f7f7 !important;}#left_column #posts .notification.last_notification,#left_column #posts .notification.single_notification {border-bottom-color:#ececec !important;}#left_column #posts .notification a {color:#565656 !important;border-bottom:0px !important;}#left_column #posts .notification blockquote {border-left-color:#eee !important;}#left_column #posts .notification .avatar {border-color:transparent !important;background-color:#fbfbfb !important;}#left_column #posts .notification .notification_type_icon{display:none !important}
}
