@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {

/* --- 4CHAN anders ---*/
/* --- GENERAL PAGE FIXES --- */


/* Blurry Thumbnails Fix */
IMG[md5] {
	height: auto !important;
	width: auto !important;
}

TABLE {
	clear: left !important;
	margin-top: 0px !important;
}


/* NAVIGATION */

body > center { display:none!important;}

#navtop {
	position: fixed !important;
	margin: 0px !important;
left: 0px !important;
margin: 0px !important;
padding: 4px !important;
position: fixed !important;
top: 0px !important;
width: 100% !important;
z-index: 3 !important;
background-color:white!important;
}

.pages {
	top: 0px !important;
	right: 0px !important;
	z-index: 3 !important;
	width: 40% !important;
	text-align:right!important;
	position: fixed !important;
	font-size:8px!important;
	background-color:white!important;
	border:none!important;

}

.pages td{padding: 0px 5px!important;}

.pages input[type="submit"] {display:none!important;}

div[style="width: 300px; position: absolute;left: 5%; border:1px solid black;"]
{ display:none!important;
 }

