css:
@-moz-document url(http://www.w3.org/TR/WAI-WEBCONTENT/) {
body {
background:none !important;
}

div.noprint, div.navbar, div.head, dl, h1, h2, ol,  h3, h4, p, dd, hr, ul.toc, ul li {
display:none;
}

dl.checkpoints, h3.guideline {
display: block;
}

dl.checkpoints dt {
margin: 0 0 1em 4.25em;
border-bottom: 1px dotted #009;
padding: 0.5em 0;
min-height: 2em;
clear: both;
line-height: 1.2;
width: 85%;
}

dt span a {
font-size: 2em;
float: left;
margin: 0 10px 20px 0;
width: 2.5em;
}

span.dfn-instance {
font-style: normal;
}

h3.guideline {
font-size: 2em !important;
width: 96% !important;
border: none !important;
background-color: #ccc !important;
margin: 1em 0 0.25em -1.5em !important;
}

a:link, a:visited, a:focus, a:hover, a:active {
text-decoration: none;
color: #000;
}




}
