css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("studivz.net") {

body {
background:#000033!important;
color:#AAAAFF!important;
font-family:Tahoma,Arial,sans-serif!important;
}

/* #######################  OBEN  ######################*/
#topheader {
background:#3366FF !important;
font-size:13px!important;
height:32px!important;
padding:0px!important;
}

#homelink {
background-image:none!important;
}

.topnav {
}

.topnav a {
background:#000066!important;
color:#AAAAFF!important;
padding:12px 5px 1px 5px!important;
font-size:9px !important;
text-transform:uppercase !important;
letter-spacing:1pt !important;
}
.topnav a:hover,.topnav a#active_menu {
background:#990000!important;
color:#FFCCCC!important;
padding:12px 5px 5px 5px!important;
font-size:9px !important;
text-transform:uppercase !important;
letter-spacing:1pt !important;
}

/* #######################  INHALT  ######################*/

#headline {
background:#990000!important;
color:#DDDDDD!important;
border-bottom:5px solid #3366FF!important;
}

h3
{
color: #AAAAFF !important;
}

#content {
background:#000066!important;
}

.secheader {
color: #DDDDDD !important;
background:#990000!important;
border-top:0px!important;
border-bottom:2px solid #3366FF!important;
}

.secheader h2 {
color:#DDDDDD!important;
text-transform:uppercase !important;
font-weight:bold!important;
}

.subheader {
background:#000066!important;
border-bottom:2px solid #3366FF!important;
color:#DDDDDD!important;
}

.subheader a {
color:#DDDDEE!important;
}


#subheadline2 {
border-left:solid 1px #990000 !important;
border-right:solid 1px #990000 !important;
}


.slimcolbox {
color:#AAAAFF !important;
/* font-size: 90% !important; */
}
.slimcolbox a, #delete_school_link a{
color:#C9520A!important;
}
.slimcolbox h4 {
color:#3366FF!important;
}

#profiletable, .label , td, #uni_name2{
color:#AAAAFF !important;
}

.status {
border:solid 1px #990000 !important;
background:#3366FF;
margin:0 20px 10px 10px !important;
padding:10px !important;
}


.content, .contentx, #headline, #content, #contentx, .content_notification {
border-left:solid 1px #990000 !important;
border-right:solid 1px #990000 !important;
}


.confirm {
background: #000033 !important;
}


/* ############## Geburtstagsbild ist zu hell... #############*/
.icon
{
display: none !important;
}

#quick_invite_box
{
background: #000066 !important;
border: solid 1px #990000 !important;
}


/* #######################  PROFILSEITE ######################*/
.myactions {
background:#000066!important;
}
.myactions a {
background:#3344FF!important;
color:#DDDDDD!important;
border:1px solid #990000!important;
}
.myactions a:hover {
color:#3366FF!important;
background:#000066!important;
}
#slimcol {
background:#3366FF!important;
}

.PrfFriendTbl a, .otherSchools a, .otherSchools Column2 a, .morefriends a{
color:#00FF00!important;
}

#userprofile {
background:#000066!important;
}

/* #######################  PINNWAND  ######################*/

#innerPinn {
}
.header {
background:#3366FF!important;
border-top:0px!important;
border-bottom:2px solid #FF3333!important;
}


.header a {
color:#AAAAFF!important;
}


.actions a {
color:#9E4007!important;
}

.actions {
border-bottom:0px!important;
}
.info {
border-top:0px!important;
border-bottom:0px!important;
}
/* #######################  START  ######################*/
.appearer2 a, .info a{
color:#C9520A!important;
}
.info h2{
border-bottom:#0099FF!important;
}


/* #######################  Meine Seite bearbeiten #############*/

#tabs {
border-bottom:2px solid #990000!important;
}
.activetab a {
background:#990000!important;
color:#DDDDDD!important;
}
.inactivetab a:hover {
background:#660000!important;
color:#FFFFFF!important;
}
.inactivetab a {
color:#222222!important;
}

h4 {
color:#DDDDDD!important;
border-bottom:2px solid #990000!important;
}
/* #######################  NAVIGATION  ######################*/

#leftlogo2 {
background-image:none!important;
height:37px!important;
}

.inputtext, .textarea {
border: 1px solid #3366FF!important;
}
#quicksearch input{
text-align:center!important;
text-transform:uppercase !important;
background-image:none!important;
padding:2px 1px 2px 1px!important;
width:118px!important;
background:#202040!important;
color:#A0A0E0 !important;
}

#leftnav,.leftnav {
border-top:solid 1px #990000 !important;
border-bottom:solid 1px #990000 !important;
margin:10px 0 !important;
padding:0;
}


.leftnav a {
color:#A0A0E0!important;
border-bottom:1px solid #222262!important;
}

.leftnav a:hover {
color:#000066!important;
background:#3366FF!important;
border-bottom:1px solid #222262!important;
}

.announcetb {
border:0px!important;
color:#A0A0E0!important;
}

.announcetb a {
color:#A0A0E0!important;
}

/* #######################  BUTTON  ######################*/
.inputsubmit, .inputbutton {
background:#3366FF!important;
color:#000099!important;
border-top:1px solid #AAAACC!important;
border-left:1px solid #AAAACC!important;
border-right:1px solid #000033!important;
border-bottom:1px solid #000033!important;
}
/* #######################  FOOTER  ######################*/

#pagefooter {
background:#000033!important;
border-top:3px solid #990000!important;
border-bottom:0px!important;
}

#pagefooter a{
color:#3366FF!important;
}
#pagefooter a:hover{
color:#000066!important;
background:#3366FF!important;
}


/* #######################  MEINE FREUNDE  ######################*/

#friendtables {
background:#000033!important;
}

#friendtables table{
background:#000066!important;
border: 0px !important;
}


.PageTabs a {
color:#9E4007!important;
}

.PageTabs a:hover {
color:#000000!important;
background:#FF9900!important;
text-decoration:none!important;
}

.friendtable {
border:0px solid #FF9900!important;
height:90px!important;
background:#000066!important;
background-color:#000066!important;
margin-top:3px!important;
margin-bottom:3px!important;
}
.friendtable a {
color:#CCCCEE!important;
}

/* forum */
#friendtable {
background:#000066 !important;
}

.image img {
height:auto!important;
max-height:90px!important;
width:50px!important;
}
#friendsecheader {
color:#BBBBCC!important;
}
/* #######################  FOTOALBUM  ######################*/

.myalbumspreviewcontainer {
background:#000066!important;
border-top:1px solid #990000!important;
border-bottom:1px solid #990000!important;
}

.myalbumspreviewcontainer div{
background:#000033 !important;
border: 0px !important;
}

.myalbumspreviewcontainer a {
color:#00FF00 !important;
}

.myalbumpreview {
background:#000099!important;
color:#AAAAFF !important;
border:0px!important;
}
.albumheadline a {
color:#000000!important;
}
.albumlinks_overview, .albumlocation_overview, .albumphotocount {
color:#AAAAFF!important;
}

.albumlinks_overview a {
color:#8D3006!important;
}
.photoheader {
border-bottom:1px solid #3366FF!important;
color:#AAAAFF !important;
}
.listfriendsfotoalbum a {
color:#8D3006!important;
}
.albumphotocount a {
color:#8D3006!important;
}
/* #######################  Nachrichtendienst ##################*/
#mailboxtabbox {
border:0px!important;
}
#mailbox_form {
background:#3366FF!important;
}
#mailbox_form th{
height:20px!important;
background:#990000!important;
}
#mailbox_form tr{
background:#000033!important;
max-height:40px!important;
}
#mailbox_form td{
border-bottom:2px solid #3366FF!important;
color:#DDDDDD!important;
}
#mailbox_form tr a{
color:#9999DD!important;
}
#mailbox_form img{
background:#3366FF!important;
height:30px!important;
}
.messagesubject a span,  #message_history_link a, #answer_recipient_full_name a{
color:#CCCCEE!important;
}
#dialog, .dialog {
border:0px!important;
}




/* lalalala */

td[class=messagetab], td[class=dialog], table[class=formtable], div[id=message_history_container], div[id=message_history_link], tr[id~=msg]
{
   font-weight: normal !important;
   background: #000033 !important;
}




/* #######################  GRUPPEN ##################*/
#contentx {
background:#0000AA!important;
/* border-left:0px!important;
   border-right:0px!important; */
}
.cat_bottom_table {
background:#3366DD!important;
border:0px!important;
}
.cat_bottom_table a {
color:#22CCEE!important;
}
#profileimage, #partyprofile {
background:#3366DD!important;
}
#group_members td {
background:#000066!important;
border:0px!important;
}
#group_members a {
color:#DDDDFF!important;
}
/* #######################  HILFE ##################*/
.coolh2 {
border-bottom:1px solid #3366FF!important;
}
.helpbox {
color:#DDDDDD!important;
border:0px!important;
background:#000099!important;
}
.helpbox a, #content a {
color:#CCCCEE!important;
}


/* ################## weg mit Yahooo ############### */

.yahoo_leftnav, .outer, div[id=quicksearch]
{
    display: none !important;
}

/* ################# jobs #################*/

.status
{
background: #000066 !important;
}

iframe[name=jobTV24]
{
display: none;
}

/* ####### teaserpic #########*/

#teaserpic
{
display:none !important;
}

/* ######## login error ####### */

#loginerrorbox, .courses_message{
color: #FFFFFF !important;
background-color: #330066 !important;
}


}
