css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.halomaps.org") {
/*Halo Maps Forum Dark Grey
    
    Contact: E3pO@wmclan.net
*/




TD, BODY {
	font-size: 11px;
background-image: none !important;
	font-family: verdana,arial,Helvetica;	
}

a:link { 
	color: #bebebe !important;
	}
a:visited { 
	color: #919191 !important;
	}
a:hover { 
	color: #636363 !important;
	text-decoration: none !important;
	}
a:active { 
	color: #bebebe !important;
	}


.mainmenulink  {

	color : #d9d9d9 !important;
	font-family : 'MS Sans Serif',Verdana,Helvetica !important;
	font-size : 12px !important;
	font-weight : bold !important;
	text-decoration : none !important;
	text-transform : uppercase !important;
}

.SMALL { font-size: 10px; }
.HEADER { 
	font-weight: bold;
         background-color: #464646 !important; 
	text-align: center !important;
	color: #d9d9d9 !important;
}
.LIGHTHEADER {
	font-weight: bold !important;
	background-color: grey !important; 
	color: black !important;
}
.LIGHT { 
	background-color: #646464 !important; 
border:1px solid black !important; 
}
BODY { 
	background-color: #646464 !important; 

}

.MID { 
	background-color: #646464 !important; 
border:1px solid black !important; 
}
.DARK { 
	background-color: #646464 !important; 


}
SELECT {
	font-family: verdana;

	font-size: 11px;
	background-color: #646464 !important;
}
INPUT, TEXTAREA { 
	font-size: 11px; 
	font-family: verdana; 

	border:1px solid red !important; 
}
.BUTTONOFF { 
	font-size: 11px; 
	font-family: verdana; 
	background-color: #646464 !important;
	color: #000000 !important;
}
.BUTTONON { 
	font-size: 11px; 
	font-family: verdana; 
	background-color: #646464 !important;
	color: #000000 !important;
}
.CHECKBOX { 
	border: none !important; 

}
.ERROR { 
	font-size:12px; 
	font-family: verdana; 
	color: #afafaf !important; 
	border: 3px double red !important; 
	font-weight: bold; 
	padding: 3px;
	background-color: #646464 !important;
}
.QUOTE {
	background-color: #646464 !important;
	border:1px dotted limegreen !important;
	font-size: 10px;
	padding: 5px;
	margin: 5px;
}

.avatar {

width: 140px;
height:7.2em;
Overflow: Auto;
}

.CODE {
	font-family: courier;
	font-size: 11px;
	color: #000000;
	background-color: #646464 !important;
	padding: 10px;
	margin: 5px;
	border: 1px solid red !important;
	width: 650px;
	Overflow: auto;
}

#messagearea {
	background-color: #646464 !important;
	font-size: 12px;
	padding:10px;
	line-height:18px;
}


.ITEM { 
	border-bottom: 1px solid yellow !important; 
	border-right: 1px solid limegreen !important; 

}
#bar
{
background-image: none !important;
background-color: #464646 !important;
border:1px solid #bebebe !important; 
height:50px !important;
}
.STATITEM
{
	border-bottom: 1px solid blue !important; 
	border-right: 1px solid green !important; 

	font-size: 13px;
	font-weight: bold ;
}

}
