css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.tvguide.com/Listings/Default.aspx"),
url("http://www.tvguide.com/listings/default.aspx"),
url("http://www.tvguide.com/Listings/"){

html{background:#000000;}

BODY,#twoColWrap{margin-left:50%;background:#000000 !important;width:800px;}

#mainBody {background:#000000;}
TD+TD{padding:0px !important;}

#twoColWrap,#twoColWrap>TABLE,
#midcol,#TVGridDiv,#gridTable,.timebarTable
{background:#000000;width:100% !important;}

DIV{background: Black none; !important}

#midcol{margin-top:-5px !important;}
#midcol>TABLE{margin-left:15% !important;}
.newSelectStyle{margin-right:1.25% !important;}

.gridDiv
{height:1400px !important;
 overflow:hidden !important;}

/* Popovers */
#foProgDescCell{background-color:white !important;}
.sumtopline,.desctitle,TD[colspan="3"]
{background-color:rgb(204,0,0) !important;}

#gridpagefooter,
#pageSubHeader,#leftcol,.gridLegend,.gridAdRow,
#midcol DIV+DIV+TABLE,#midcol DIV[style*="height=2px"],
.ccTopAdContainer+CENTER,#listingsPage+SCRIPT+SCRIPT+CENTER,
#twoColWrap>TABLE>TBODY>TR>TD:first-child,
.ghead-w,.ghead-w+SCRIPT+DIV+DIV+DIV,
.shop-noncontextual,#tivoreclink,#AmazonRow,
sumtopline+.cornerimage,.sumtopline,
IMG[src$="top-left.gif"],IMG[src$="top-right.gif"]
/* HDTV button and CC info */
,#hdtvToggleImg,.cc,IMG[width="88"]
{display:none !important;}
}

@-moz-document url-prefix(http://www.tvguide.com/Listings/Grid),
url-prefix(http://www.tvguide.com/listings/Grid){
FORM DIV+DIV,DIV TABLE+TABLE,#TvGridDiv,#gridDiv
{width:100% !important;}
FORM DIV+DIV TABLE[width="100%"],.sr,
.gridLegend,.gridRow+.timebarRow,.cc
{display:none !important;}
}

@-moz-document domain("tvguide.com") {
#body,#mainBody,#obj-w{width:100% !important;}
#pageSubHeader *{font-size:medium !important;}
#obj-lh-w{width:65% !important;}
#obj-rh-w{width:35% !important;}
.obj-lb-w,.obj-rb-w,.obj-lb-h,.obj-rb-h,
.episodeList
{width:100% !important;}
.obj-lb-h,.obj-rb-h{background-repeat:repeat-x !important;}
*{font-size:x-small !important;}
.ccTopAdContainer,#topNavContainer,
#footer,#ad_270,.options,.ratings>A,
.obj-lb-c>A,.imageCredit,#adRotator 
{display:none !important;}
}
