css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("ogame.com.es") {



/* @@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@

Atributos base

X[class="X"]

color: #FFFFFF !important;

font-size: 11px !important;

text-decoration: none !important;

font-weight: bold !important;

@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@ */













/* --------- GENERAL --------- */



#playerName span.textBeefy {color: #FF00FF !important;}



#errorBoxDecision h4#errorBoxDecisionHead

{color: #FFFF00 !important; font-size: 20px !important; text-decoration: blink;}

#errorBoxDecision p#errorBoxDecisionContent

{color: #FFFFFF !important; font-size: 14px !important;}



/* RECURSOS */

#resources  span.overmark

{color: #FF0000 !important; font-weight: bold !important;}

#resources  span.middlemark

{color: #FFA000 !important; font-weight: bold !important; text-decoration: blink; font-size: 11px !important;}

#resources span#resources_metal

{color: #AAAAAA; font-size: 10px;}

#resources span#resources_crystal

{color: #9999FF;font-size: 10px;}

#resources span#resources_deuterium

{color: #00CC00;font-size: 10px;}

#resources span#resources_energy

{color: #FFFFFF;font-size: 10px;}

#resources span#resources_darkmatter

{color: #CC55CC;font-size: 10px;}



#changelog_link {font-size: 0px !important; border: 0px !important;}





/* Script Recursos en vuelo */

#eventContent #resourcesInFlight {margin-left: 40px !important;}

#resourcesInFlight {padding-bottom: 15px; padding-top: 15px; text-align: left !important; margin-left: 6px !important;}





/* ANTIGAME */

a[id="btnAntiOptions"] span.textlabel {color: #9999FF !important;}



/* ANTIGAME Caja Recursos */

div#links li.antires b  /* Titulo */

{color: #BBBBBB !important; font-weight: bold !important; font-size: 12px !important;}

div#links li.antires span[class=""]

{color: #AAAAAA !important;}

div#links li.antires span.middlemark /* Casi lleno */

{color: #FFA000 !important; font-weight: bold !important;}

div#links li.antires  span.overmark /* Lleno */

{color: #DD0000 !important; font-weight: bold !important;}

div#links li.antires span.undermark /* Produccion */

{color: #00FF00 !important; font-weight: bold !important; font-size: 10px !important}

div#links li.antires span.finishtime /* Tiempo de llenado */

{color: #FFFFFF !important;}

div#links li.antires span#antires_metal 

{color: #999999 !important; font-weight: bold !important;}

div#links li.antires span#antires_crystal 

{color: #9999FF !important; font-weight: bold !important;}

div#links li.antires span#antires_deuterium 

{color: #00CC00 !important; font-weight: bold !important;}





/* Botones extra Mensajes (Antigame) */

#message input[value="Xx"]  {display:none !important;}

#message input[value="V"]  {color: #00aa00 !important; font-size: 13px !important;}

#message input[value="VV"]  {color: #00FF00 !important;  font-size: 13px !important;}

#message input[value="X"]  {color: #bb0000 !important;  font-size: 13px !important;}

#message input[value="XX"]  {color: #ff0000 !important;  font-size: 13px !important;}





/* Recursos Necesarios */

div#deficient 

{background-color: #3b526b !important; border: 1px solid #444444 !important;}



div#deficient th[colspan="2"] 

{color: #FFFF00 !important; font-weight: bold !important; text-decoration: underline;}

div#deficient td.metal 

{color: #BBBBBB !important; font-weight: bold !important;}

div#deficient td.crystal 

{color: #9999FF !important; font-weight: bold !important;}

div#deficient td.deuterium 

{color: #00DD00 !important; font-weight: bold !important;}



div#deficient table tbody tr + tr td

{color: #AAAAAA !important;}

div#deficient table tbody tr + tr + tr td

{color: #7777FF !important;}

div#deficient table tbody tr + tr + tr + tr td

{color: #00BB00 !important;}



div#deficient table + table tbody tr + tr td

{color: #FFFFFF !important;}

div#deficient table + table tbody tr + tr td + td

{color: #ffa000 !important; font-weight: bold !important;}



div#deficient table + table tbody tr + tr + tr td

{color: #FFFFFF !important;}

div#deficient table + table tbody tr + tr + tr td + td

{color: #ffa000 !important; font-weight: bold !important;}





/* SCRIPT SATs */

.detail_screen span#missingSats

{color: #CC0000 !important; font-size: 11px !important;font-weight: bold !important;text-decoration: blink !important;}

.detail_screen span#missingSats:hover

{color: #9999FF !important;}











/* --------- VISION GENERAL --------- */



/* EVENT LIST SUPERIOR */

#eventtype span#eventFriendly {font-weight: bold !important;}

#eventtype span#eventNeutral {font-weight: bold !important;}

#eventtype span#eventHostile {font-weight: bold !important;}

#eventNeutral {color: #9999FF;}

td[class="neutral col2"] {color: #9999FF !important;}



.countDown span[class="hostile textBeefy"]

{color: #FF0000 !important; font-size: 11px !important; text-decoration: blink;}

 



/* INFO PLANETA */

#planetDetails td.data #positionContentField a {color: #ffffff !important;}

#planetDetails td.data #positionContentField a:hover {color: #9999FF !important; text-decoration: none !important;}

#planetDetails td.data #scoreContentField a {color: #ffffff !important;}

#planetDetails td.data #scoreContentField a:hover {color: #FFA000 !important; text-decoration: none !important;}



/* ACTUALES */

table.construction span.level {color: #00FF00 !important; font-weight: bold !important;}

table.construction span#Countdown {color: #00bb00 !important;}

table.construction span#researchCountdown {color: #00bb00 !important;}

table.construction td.finishTime {color: #777777 !important;}

table.construction td.idle {font-size: 0px !important;}

table.construction #shipSumCount {color: #FFFF00 !important; font-weight: bold !important;}











/* --------- FLOTA --------- */



/*  1-INF */

/* Recursos */

div[class="fleft total_capacity"] table tbody tr + tr td.capacity_key 

{color: #aaaaaa!important;}

div[class="fleft total_capacity"] table tbody tr + tr td.capacity_key + td

{color: #bbbbbb!important; font-weight: bold !important; text-decoration: none !important;}



/* Capacidad */

div[class="fleft total_capacity"] table tbody tr + tr + tr td.capacity_key 

{color: #00aa00 !important;}

div[class="fleft total_capacity"] table tbody tr + tr + tr td.capacity_key + td#total_capacity

{color: #00bb00 !important; font-weight: bold !important; text-decoration: none !important;}



/* CP */

div[class="fleft total_capacity"] table tbody tr + tr + tr + tr td.capacity_key 

{color: #8888BB !important;}

div.allornonewrap a#SCargo

{color: #8888BB !important; font-weight: bold !important;}

div.allornonewrap a#SCargo:hover {color: #BBBBFF !important; text-decoration: none !important;}

div.allornonewrap a#SCargo:before {content: "[" !important;}

div.allornonewrap a#SCargo:after {content: "]" !important;}



/* CG */

div[class="fleft total_capacity"] table tbody tr + tr + tr + tr + tr td.capacity_key 

{color: #8888BB !important;}

div.allornonewrap a#LCargo

{color: #8888BB !important; font-weight: bold !important;}

div.allornonewrap a#LCargo:hover {color: #BBBBFF !important; text-decoration: none !important;}

div.allornonewrap a#LCargo:before {content: "[" !important;}

div.allornonewrap a#LCargo:after {content: "]" !important;}



/* EXP-P */

div[class="fleft total_capacity"] table tbody tr + tr + tr + tr + tr + tr td.capacity_key 

{color: #7B68EE !important;}

div[class="fleft total_capacity"] table tbody tr + tr + tr + tr + tr + tr td.capacity_key + td

{color: #AB99FF !important; font-weight: bold !important;}



/* Velocidad */

div[class="fleft total_capacity"] table tbody tr + tr + tr + tr + tr + tr + tr td.capacity_key 

{color: #cccccc !important;}

div[class="fleft total_capacity"] table tbody tr + tr + tr + tr + tr + tr + tr td.capacity_key + td

{color: #FFFFFF !important; font-weight: bold !important;}









/* FLOTAS 2 */



#fleet2 span#maxspeed

{color: #FFFFFF !important; font-weight: bold !important;}

#fleet2 #fleetBriefingPart1 li 

{color: #BBBBBB !important; font-weight: normal !important;}

#fleet2 #fleetBriefingPart1 li > span

{color: #BBBBBB !important;}



#fleet2 select#speed.speed {color:#FFFFFF !important;}

#fleet2 select#speed.speed option[value="9"]  {color:#33FF00}

#fleet2 select#speed.speed option[value="8"]  {color:#66FF00}

#fleet2 select#speed.speed option[value="7"]  {color:#99FF00}

#fleet2 select#speed.speed option[value="6"]  {color:#CCFF00}

#fleet2 select#speed.speed option[value="5"]  {color:#FFFF00}

#fleet2 select#speed.speed option[value="4"]  {color:#FFCC00}

#fleet2 select#speed.speed option[value="3"]  {color:#FF9900}

#fleet2 select#speed.speed option[value="2"]  {color:#FF6600}

#fleet2 select#speed.speed option[value="1"]  {color:#FF3300}



#fleet2 span#duration

{color: #9999FF !important; font-weight: bold !important;}

#fleet2 #fleetBriefingPart1 li + li

{color: #8888DD !important; font-weight: normal !important;}

#fleet2 #fleetBriefingPart1 li + li > span

{color: #8888DD !important;}



#fleet2 span#consumption .undermark

{color: #00CC00 !important; font-weight: bold !important;}

#fleet2 #fleetBriefingPart1 li + li + li

{color: #00aa00 !important; font-weight: normal !important;}

#fleet2 #fleetBriefingPart1 li + li + li > span

{color: #00aa00 !important;}

#fleet2 span#consumption .overmark

{color: #FF0000 !important; font-weight: bold !important; text-decoration: blink;}



#fleet2 span#arrivalTime, #fleet2 span#returnTime

{color: #DDDDDD !important; font-weight: bold !important;}

#fleet2 #fleetBriefingPart1_2 li, #fleet2 #fleetBriefingPart1_2 li + li

{color: #BBBBBB !important; font-weight: normal !important;}

#fleet2 #fleetBriefingPart1_2 li > span, #fleet2 #fleetBriefingPart1_2 li + li > span

{color: #BBBBBB !important;}



#fleet2 span#storage .undermark

{color: #DDDD00 !important; font-weight: bold !important;}

#fleet2 #fleetBriefingPart1_2 li + li + li

{color: #BBBBBB !important; font-weight: normal !important;}

#fleet2 #fleetBriefingPart1_2 li + li + li > span

{color: #BBBBBB !important;}





/* FLOTAS 3 */



/* Falta SAC (li+2), HOLD (li+6) */



#fleet3 ul li

{color: #DD9000 !important; font-weight: normal !important;}

#fleet3 ul li > span

{color: #FFA000 !important; font-weight: bold !important;}



#fleet3 span#duration

{color: #9999FF !important; font-weight: bold !important;}

#fleet3 ul li + li + li

{color: #8888DD !important; font-weight: normal !important;}

#fleet3 ul li + li + li > span

{color: #8888DD !important; font-weight: normal !important;}



#fleet3 span#arrivalTime, #fleet3 span#returnTime

{color: #DDDDDD !important; font-weight: bold !important;}

#fleet3 ul li + li + li + li, #fleet3 ul li + li + li + li + li

{color: #BBBBBB !important; font-weight: normal !important;}

#fleet3 ul li + li + li + li > span, #fleet3 ul li + li + li + li + li > span

{color: #BBBBBB !important; font-weight: normal !important;}



#fleet3 span#consumption .undermark

{color: #00CC00 !important; font-weight: bold !important;}

#fleet3 ul li + li + li + li + li + li + li + li

{color: #00aa00 !important; font-weight: normal !important;}

#fleet3 ul li + li + li + li + li + li + li + li > span

{color: #00aa00 !important; font-weight: normal !important;}



#fleet3 span#expeditiontimeline

{color: #9999FF !important; font-weight: bold !important;}

#fleet3 ul li + li + li + li + li + li + li

{color: #8888DD !important; font-weight: normal !important;}

#fleet3 ul li + li + li + li + li + li + li > span

{color: #8888DD !important; font-weight: normal !important;}



#fleet3 li#expeditiontimeline option  

{color:#9999FF; background-color: #000022 !important;}

#fleet3 li#expeditiontimeline option:hover  

{color:#BBBBFF; background-color: #6666AA !important;}

#fleet3 li#expeditiontimeline select 

{color:#9999FF; background-color: #000022 !important;  font-size: 11px !important;}



#fleet3 #info ul li + li + li

{color: #FFFFFF !important;}





/* --------- MOVIMIENTO FLOTA --------- */



/* MISIONES MOV. FLOTA */



/* Misiones de ataque */

.fleetDetails .hostile {color: #FF0000 !important; font-size: 11px !important; text-decoration: blink;}

/* Amistosas, propias, retorno */

.fleetDetails .friendly {color: #ffffff !important; font-size: 11px !important;}

/* No ataques */

.fleetDetails .neutral {color: #9999ff !important; font-size: 11px !important;}



.targetName {color: #ffffff !important;} /* Nick jugador */



.timer {color: #00FF00 !important; font-weight: bold !important;} /* Cuenta atras 1 */

.absTime {color: #AAAAAA !important;} /* Hora de llegada */

.nextTimer {color: #00FF00 !important; font-weight: bold !important;} /* Cuenta atras 2 */

.nextabsTime {color: #AAAAAA !important;} /* Hora de retorno */

.reversalTime {color: #FFA000 !important;}



.originPlanet {color: #9999ff !important;} /* Planeta de origen */

.destinationPlanet {color: #ff5555 !important;} /* Planeta destino */



.fleetStatus span {color: #aaaaaa !important;} /* Titulos */

.fleetStatus span.current {color: #ff0000 !important;} /* Huecos utilizados */

.fleetStatus span.all {color: #00ff00 !important;} /* Huecos maximos */















/* --------- GALAXIA --------- */



#galaxyContent #probeValue {color: #FF00FF !important; font-weight: bold !important;}

#galaxyContent #recyclerValue {color: #e0830a !important; font-weight: bold !important;}

#galaxyContent #missileValue {color: #FF0000 !important; font-weight: bold !important;}

#galaxyContent #slotValue {color: #999999 !important; font-weight: bold !important;}

#galaxyContent #slotValue #slotUsed {color: #FFFFFF !important; font-weight: bold !important;}



/* Colores Galaxia */

td.planetname {color: #AAAAAA !important; font-size: 10px !important;}

td.allytag span a {color: #9999FF !important; font-size: 10px !important;}

span.status_abbr_active {color: #dddddd !important; font-size: 10px !important;}



span.status_abbr_inactive 

{color: #ffff00 !important; font-size: 12px !important;font-weight: bold !important;text-decoration: none !important;text-decoration: underline !important;}



span.status_abbr_longinactive 

{color: #ff6000 !important; font-size: 12px !important;font-weight: bold !important;text-decoration: none !important;text-decoration: underline !important;}



span.status_abbr_vacation {color: #7777ff !important; font-size: 10px !important;}

span.status_abbr_strong {color: #ff0000 !important; font-size: 10px !important;}

span.status_abbr_noob {color: #00ff00 !important; font-size: 10px !important;}

span.status_abbr_banned {color: #bb0000 !important; font-size: 11px !important;}



/*span.anti_rank {color: #FF0000 !important; font-size: 10px !important;}*/

/*span.anti_allyrank {color: #AAAAAA !important; font-size: 10px !important;}*/

td.planetname span.undermark {color: #FF3333 !important; font-size: 11px !important;font-weight: bold !important; text-decoration: blink;}

div.anti_debris {color: #BBBBBB !important; font-size: 10px !important;font-weight: bold !important;}









/* GMARK - Marcador de Galaxia */



span#changeCase input[type="button"] {display:none !important;}

span#changeCase {color: #9999FF !important; font-weight: none !important;}

span#changeCase:hover {color: #FFF000 !important; font-weight: none !important;}

span.marqueur input[type="button"]

{font-size: 9px !important; border-color:#22313b !important; margin-left: 1px !important;}



div.note th.area input[type="button"]

{font-size: 11px !important; border-color:#22313b !important; margin-left: 1px !important;}

div.note th.area input[type="button"]:hover

{border-color:#6f9fc8 !important;}











/* --------- MENSAJES --------- */



div.note Font[color="red"] 

{font-weight: bold !important; text-decoration: blink !important;}

div.note a 

{color: #DDA000 !important; font-size: 12px !important; font-weight: bold !important;}

div.note a:hover 

{color: #FFFF00 !important; font-size: 12px !important; text-decoration: none !important; font-weight: bold !important;}

div.note th.area

{color: #6f9fc8 !important; font-size: 12px !important; font-weight: bold !important;}



div.infohead span.new span

{color: #00FF00 !important; font-size: 11px !important; font-weight: bold !important; background-color: transparent !important; text-decoration: blink !important;}



td.subject

{color: #AAAAAA !important;}











/* --------- MAS --------- */



a[href="http://board.ogame.com.es/index.php?page=Index"]

{color: #9999FF !important; font-weight: bold;}



/* A tomar por culo la estrellita */

#star2, #star, #star1 {display:none !important;}





/* @@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@ */

}
