css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.overclockers.ua") {

/* BODY + GENERAL */
body { min-width: 960px; color: #383b3f !important; background: #383b3f !important; }
body > table { max-width: 1200px; margin: 0 auto; }
body > div:last-child { display: none; }
blockquote { margin: 0 6em 0 3em; padding: 5px 20px 10px 40px; background-color: #b6c6db !important; -moz-box-shadow: 0 -1px 0 #98abc3 inset; -moz-border-radius: 6px; }
hr:last-child { display: none; }




::-moz-selection { color: #fff !important; background: #98abc3 !important; }

/* BODYLINE */
table > .bodyline { color: #fff !important; }
.bodyline { font-family: Arial !important; color: #383b3f !important; background: #383b3f !important; }
.bodyline a { color: #468 !important; text-decoration: underline !important; }
.bodyline a:focus { outline: 0 !important; }
.bodyline a:hover { color: #407840 !important; }
.bodyline img[src="templates/subSilver/images/printer.gif"] { display: none; }
.bodyline > table:first-child { display: none; }

/* TOP */
.topgray { -moz-border-radius: 10px 0 0 10px; font: 14px/20px Arial !important; background-color: #F2F1E1 !important; white-space: nowrap; }
.toporange { -moz-border-radius: 0 10px 10px 0; font: 14px/20px Arial !important; background-color: #F2F1E1 !important; white-space: nowrap; }
.topgray a, .toporange a { color: #383b3f !important; text-decoration: underline !important; }
.topgray a:hover, .toporange a:hover { color: #407840 !important; }

/* TH */
th { color: #383b3f !important; background-color: #9397a2 !important; }
th:first-child { -moz-border-radius-topleft: 5px; }
th:last-child { -moz-border-radius-topright: 5px; }

/* FORUMLINE */
.forumline { margin: 5px 0; border-width: 0; border: 1px solid #fff !important; -moz-border-radius: 6px !important; background-color: #cdcdcd !important; }
.forumline th { font: 17px/20px Arial !important; text-shadow: 1px 1px 0 #bbb; }
.forumline .thTop:first-child { cursor: default; text-shadow: 1px 1px 0 #fff; background-color: #dfdfdf !important; }
.forumline .thTop:first-child .thTop { padding: 0 !important; }
.forumline td { font: 14px/20px Arial !important; background-color: #cdcdcd !important; }
.forumline td.row1 { padding: 0 !important; background-color: #cdcdcd !important; -moz-border-radius: 5px; }
.forumline td.row1 td, .forumline td.row2 { -moz-border-radius: 5px; }
.forumline td.catLeft { padding-left: 36px !important; background-color: #b2b4b8 !important; -moz-border-radius: 5px; -moz-box-shadow: 0 -1px 0 rgba(64,73,91,0.5) inset; }
.forumline tr:nth-child(2) td.catLeft { -moz-border-radius: 0 0 5px 5px; }
.forumline td.catHead { -moz-border-radius: 10px; }



/* GENMED */
a.genmed { font: 13px/20px Arial !important; color: #383b3f !important; }
a.genmed:hover { color: #db3627 !important; }
span.genmed b { font: 16px/18px Arial !important; }



/* GENSMALL */
a.gensmall, 
.gensmall a, 
#extra_smilies_msg a { display: inline-block; padding: 2px 6px; font: 14px/20px Arial !important; color: #6589ac !important; }
a.gensmall:hover, 
.gensmall a:hover, 
#extra_smilies_msg a:hover { color: #fff !important; background: #6589ac; -moz-border-radius: 2px; -moz-box-shadow: 0 -1px 0 #468 inset; }

/* GENSMALL */
.forumline a.gensmall, 
.forumline .gensmall a, 
.forumline #extra_smilies_msg a { display: inline; font: 14px/20px Arial !important; color: #468 !important; }
.forumline a.gensmall:hover, 
.forumline .gensmall a:hover, 
.forumline #extra_smilies_msg a:hover { color: #393 !important; background: transparent !important; -moz-box-shadow: none; }



/* NAV */
.nav, .nav b { display: inline-block; font: 16px/20px Arial !important; color: #ccc !important; }
a.nav, .nav a, .gen a, .nav b  { display: inline-block; padding: 2px 6px; font: 16px/20px Arial !important; color: #595 !important; -moz-border-radius: 2px; }
a.nav:hover, .nav a:hover, .gen a:hover, .nav b { color: #fff !important; background: #407840; }
.gen a { color: #6589ac !important; }
.gen a b { font: 16px/20px Arial !important; color: #6589ac !important; }
.gen a:hover b { color: #fff !important; background: #407840; -moz-border-radius: 2px; }



/* FORUMLINK */
span.forumlink + span.genmed { color: #666 !important; }
span.forumlink a, a.forumlink, td.gen a { font: 18px/20px Arial !important; }
td.gen a { color: #595 !important; }
td.gen a:hover { color: #db3627 !important; }
span.forumlink a, a.forumlink { color: #383b3f !important; }
span.forumlink a:hover, a.forumlink:hover { color: #407840 !important; }



/* TOPIC TITLE */
a.topictitle { font: 16px/18px Arial !important; color: #2b3035 !important; }



/* NAME + CATEGORY TITLE */
.name a, .cattitle { font: 16px/20px Arial !important; }
.name a:hover { color: #d24848 !important}
.cattitle a { font: 14px/20px Arial !important; color: #40495b !important; }



/* POST DETAILS */
.postdetails { font: 11px/14px Arial !important; color: #666 !important; }
.postdetails a { font: 12px/14px Arial !important; }

/* POST BODY*/
.postbody { display: block; padding: 0.5em; margin: 0 0.25em 0 0; font: 16px/20px Arial !important; }
.postbody img { vertical-align: top; max-width: 100%; }
.postbody + .postbody { display: none; }
span.off { font: 14px/18px Arial !important; color: #666 !important; }



/* FORMS */
input[type=button], input[type=submit] { cursor: pointer; padding: 4px 10px; font-variant: small-caps; font-size: 16px !important; }

input[type=submit] { border: 0; -moz-border-radius: 20px !important; -moz-box-shadow: 0 0 1px #aaa inset; color: #40434c !important; background: -moz-linear-gradient(center top , #fff, #dfdfdf); }
input[type=submit]:hover { color: #1f2229 important; background: -moz-linear-gradient(center top , #fff, #cecece); -moz-box-shadow: 0 0 5px #a6a6a6; }

input[type=button] { border: 0; -moz-border-radius: 10px !important; font: 16px/20px Arial !important; -moz-box-shadow: 0 0 1px #aaa inset; color: #333 !important; background: -moz-linear-gradient(center top , #fff, #dfdfdf); }
input[type=button]:hover { -moz-box-shadow: 0 0 5px #a6a6a6; background: -moz-linear-gradient(center top , #fff, #cecece); }

select { padding: 2px; -moz-border-radius: 3px; -moz-box-shadow: 0 0 1px #aaa inset; font: 16px/16px Arial !important; }
select:focus { -moz-box-shadow: 0 0 8px #5699D8; outline: none !important; }

textarea { -moz-border-radius: 20px; padding: 10px; -moz-box-shadow: 0 0 1px #666 inset; font: 14px/18px Arial !important; border: 1px solid #666 !important; }
textarea:focus { border-color:#069 !important; outline:0 !important; }

input[type=text], input[type=password] { width: 215px; padding: 4px 10px; font: 14px/20px Arial !important; background: #efefef !important; -moz-box-shadow: 0 0 1px #666 inset, 1px 2px 3px #ccc inset; border: 0; -moz-border-radius: 20px; }
input[type=text]:hover, input[type=password]:hover { background: #fefefe !important; }
input[type=text]:focus, input[type=password]:focus { background: #fefefe !important; -moz-box-shadow: 0 0 1px #069 inset, 1px 2px 3px #ccc inset; }

textarea.post { min-width: 600px !important; min-height: 400px !important; }

/* FORMS */
.forumline td.row1:first-child span.name, .forumline td.row2:first-child span.name { display: block; padding: 0.25em 0.5em 0; }
.forumline td.row1:first-child span.name + br, .forumline td.row2:first-child span.name + br { display: none; }
.forumline td.row1:first-child span.postdetails, .forumline td.row2:first-child span.postdetails { display: block; padding: 0 0.5em; }
.forumline td.row1:first-child span.postdetails img, .forumline td.row2:first-child span.postdetails img { margin: -8px 0; padding: 2px; background: #fff; border: 1px solid #40495b !important; }
.forumline td.row1:first-child span.postdetails a.gensmall, .forumline td.row2:first-child span.postdetails a.gensmall { color: #270 !important; }



/* DIFICULT STYLES */
.forumline td.row1:nth-child(2) table , .forumline td.row2:nth-child(2) table { border-collapse: collapse; }

.forumline td.row1:nth-child(2) tr:first-child td, .forumline td.row2:nth-child(2) tr:first-child td { padding: 0.25em 1em !important; -moz-border-radius: 0 !important; }

.forumline td.row1:nth-child(2) tr:first-child td.gensmall a.gensmall:nth-child(1), .forumline td.row2:nth-child(2) tr:first-child td.gensmall a.gensmall:nth-child(1) { color: #407840 !important; margin-right: -12px; }
.forumline td.row1:nth-child(2) tr:first-child td.gensmall a.gensmall:hover:nth-child(1), .forumline td.row2:nth-child(2) tr:first-child td.gensmall a.gensmall:hover:nth-child(1) { color: #db3627 !important; }

.forumline td.row1:nth-child(2) tr:first-child td.gensmall a.gensmall:nth-child(2), .forumline td.row2:nth-child(2) tr:first-child td.gensmall a.gensmall:nth-child(2) { display: none; }

}
