css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kinozal.tv") {

body { font-family: Arial !important; overflow-x: hidden; }

a:link, a:visited { color: #058 !important; text-decoration: underline !important; }
a:hover, a:active { color: #0a0 !important; }

a.navt { display: block; margin: 0.25em 0.5em; font-size: 18px !important; text-shadow: 0 0 3px #eee; }
a.navt font { color: #383838 !important; font-weight: normal; }
a.navt:hover font { color: #080 !important; }

table.mn { -moz-border-radius: 5px; -moz-box-shadow: 0 -1px 0 rgba(100,100,0,0.3) inset; }
.mn img { -moz-box-shadow: 0 0 10px #ccc; }
.mn2 { background: #fff !important; }
.mn2, .mn2 table { -moz-border-radius: 5px; line-height: 18px; }
.mn2 table td { font-size: 12px !important; }
td.templ { padding: 10px 20px 10px 10px !important; -moz-box-shadow: 0 -1px 0 rgba(0,100,0,0.10) inset; background: rgba(90,90,90,0.03); -moz-border-radius: 0 20px 20px 0; }
td.templ a { color: #2b3035 !important; font-size: 16px !important; line-height: 1.2em; font-weight: normal !important; text-decoration: underline !important; }
td.templ a:hover { color: #ff2d00 !important; }
td.templ a:hover font { color: #2e9700 !important; }

input[type=text] { border: 0 !important; background-color: red; }

select { font-size: 12px !important; font-family: Arial !important; }

input[type=submit], input[type=reset], input[type=button], button { font-size: 14px !important; font-family: Arial !important; border: 1px solid #ccc !important; background: -moz-linear-gradient(center top, #f9f9f9, #e3e3e3) repeat scroll 0 0 transparent !important; }



}
