css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {







#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, 

#dashboard_followers, #dashboard_members{





body {








background-image:url('http://29.media.tumblr.com/tumblr_laandc4v361qe0tq3o1_500.jpg') center right #000000 repeat fixed !important;





  }





background-repeat:repeat-x !important;

background-attachment:fixed;

background-position: top right;



background-color:#030303 !important;



background-position:top !important



}







#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, 

#dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background: rgba(10,10,10,0.6) !important;



}







#dashboard_edit_post{



background-color: #08090e !important



}







#right_column .dashboard_nav_item {



border-bottom:0px solid #eee !important;



background-image:none  !important



}







#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#beb5a1 !important;



}







#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {



                        color:#888 !important ;



                    }







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {



                        background: none !important;



                    }



#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#b2a160 !important;



opacity:0.4 !important;



                    }







#left_column #posts .notification .avatar:hover{



opacity:0.6 !important;



                    }



.post{opacity:1 !important; border-bottom:0px !important; color:#333}



.post:hover{opacity:1 !important}



.permalink{background-color: rgba(10,10,10,0.9) !important; background-image:none !important}



.active{background-color:#b2a160 !important}



.nav_item_nipple{display:none !important}



.nav_menu{background-color:#b2a160 !important; border-bottom:0px !important}







#nav .nav_item.active,



#nav .nav_item .nav_menu,



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



background-color:#b2a160 !important;



background-image:none !important;



border:0px !important



}







#nav .nav_item .nav_menu {



border-bottom:0px !important



}







#nav .nav_item .nav_menu a {



color:#c18d6a !important;



border-top-color:#322115 !important;



}







#nav .nav_item .nav_menu a:hover {



color:#fbfbfb !important;



}







.dashboard_switch_blog_menu_header{



background-color:#b2a160 !important;



color:#666 !important;



}



.dashboard_switch_blog_menu_item{



background-color:#b2a160 !important;



color:#666 !important;



border:0px !important



}



.dashboard_switch_blog_menu_footer{



background-color:#b2a160 !important;



color:#666 !important;



}



.dashboard_switch_blog_menu_wrapper{



background-color:#b2a160 !important;



}







#new_post_notice_container{



color:#fff !important



}







#new_post_notice_container a{



color:#fff !important



}







#dashboard_switch_blog_menu_current,



#dashboard_switch_blog_arrow_and_divider,



#dashboard_switch_blog_arrow_current



{



background-image:none !important



}







#footer{



background: rgba(10,10,10,0.6) !important;



background-image:none !important



}















ol#posts li.post.new_post {



background: #ffffff url('http://26.media.tumblr.com/tumblr_la935hm7hj1qe0tq3o1_500.png') center center no-repeat;



}







img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{



    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;



}



.new_post_label{



font-size:14px !important;



padding-top:5px !important;



font-family:Arial, Times New Roman, sans-serif !important;



font-weight:bold !important;



text-transform: lowercase !important;



color:#222 !important;



letter-spacing:1px !important



}



#left_column #posts .notification {



                        background-color:#b2a160 !important;



                        color:#44412c !important;



                        border-bottom-color: #44412c !important



                    }







#left_column #posts .notification .block {



                            color:#44412c !important;



                        }







#left_column #posts .notification.alt {



                        background-color: #1e1912 !important;



border-bottom-color: #213825 !important



                    }



#left_column #posts .notification.last_notification,



                    #left_column #posts .notification.single_notification {



                        border-bottom-color:#44412c !important;



                    }



#left_column #posts .notification a {



                        color:#44412c !important;



                        border-bottom:0px !important;



                    }



#left_column #posts .notification blockquote {



                        border-left-color:#eee !important;



                    }







#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#fbfbfb !important;



opacity:0.5 !important;



                    }







#left_column #posts .notification .avatar:hover{



opacity:1 !important;



                    }



.notification_type_icon{display:none !important}







h1,h2,h3,h4,h5,h6{color:#b2a160 !important}



}
