css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("justin.tv") {


body {
	background: #FFF !important;
}


/* ADS */
#ChanMedRectv2,
#DirectoryMedRectv2,
#DirectoryLeaderBottomv2,
#SearchMedRect,
#SearchLeaderBottomv2,
#ChanLeaderBottomv2,
#aus,
#adaptvBannerAd,
#adaptvBannerFrame {
	display: none !important;
}



/* WRAPPER */
.wrapper {
	width: 69.5em !important;
}
.wrapper .left_col {
	color: #666 !important;
	margin-right: 0.5em !important;
}
.wrapper .left_col a {
	color: #00D !important;
}
.wrapper .left_col .info_pic,
.wrapper .left_col .action_arrow {
	display: none !important;
}
.wrapper .left_col .col_bg {
	background: transparent !important;
}
.wrapper .left_col .cntr,
.wrapper .left_col .simple_chan_sect,
#actions,
#about,
#stats,
#data_tabs ul.tabs {
	border: 1px solid #CCC !important;
	background: #F1F1F1 !important;
	margin: 5px 0 !important;
	padding: 2px 5px !important;
}
#banner_custom, #banner_default {
	display: none !important;
}
#data_tabs {
	margin: 0 !important;
	width: 100% !important;
	background: transparent !important;
}

#status, #description, #fans, #badges, #networks {
	font-family: Georgia, Helvetica, Arial;
	border: 0 !important;
	background: transparent !important;
}
#action_links {
	border: 0 !important;
}
#action_contents {
	background: transparent !important;
}
#stats h3 {
	font-family: Georgia, Helvetica, Arial;
	font-size: 1em !important;
}
#chat_container, #related {
background: transparent !important;
	border: 1px solid #CCC !important;
}
#chat_container {
	margin-top: 110px !important;
}
#chat_container .chat_box,
#related_list,
#related .chan_sect_title {
	background: #F1F1F1 !important;
}
#ad_holder, #ChanHeader_holder{
	display: none !important;
}



/* DIRECTORY */
#directory .left_col {
	width: 50em !important;
}
#directory #right_col {
	display: none !important;
}
#dir_filters {
	width: 45em !important;
}
#directory .list_item {
	text-align: center !important;
	background: #F1F1F1 !important;
	padding: 5px !important;
	border: 1px solid #CCC !important;
}
#directory .list_item .title {
	font-size: 0.8em !important;
	font-weight: bold;
}
#directory .list_item .small {
	font-size: 0.7em !important;
}


/* SEARCH */
#search_results li.sponsored_result,
#search_results .right_col {
	display: none !important;
}
#search_results ul.search_list li.list_item {
	background: #F1F1F1 !important;
	padding: 5px !important;
	border: 1px solid #CCC !important;
	margin: 5px 0 !important;
}
#search_results .list_item .content{
	width: 32em !important;
}
#search_results .list_item .content p {
	font-size: 0.82em !important;
}


/* FOOTER */
#footer {
	background: #FFF !important;
	margin: 0 auto !important;
	font-size: 0.7em !important;
}
}
